Cargo depot.script:
{_ Cargo depot }
{_ Triggered when collecting or delivering cargo for a wing mission }


{if event.updatetype = "Collect":
    You have collected {event.amount} tonne{if event.amount != 1:s} of {event.commodity} from the mission depot.
|elif event.updatetype = "Deliver":
    You have delivered {event.amount} tonne{if event.amount != 1:s} of {event.commodity} to the mission depot.
}

{if event.updatetype != "WingUpdate":
    {Pause(500)}
    {set remaining to event.totaltodeliver - event.delivered}
    {if remaining = 0:
        Wing mission requirements are now completed.
    |elif event.updatetype = "Deliver":
        Wing mission is now {round(event.delivered / event.totaltodeliver * 100, 0)} percent complete.
    }
}

Cargo report.script:
{_ Cargo report }
{_ Report current cargo manifest }

{set cargocarried to 0}
{set haulage to 0}
{set illegal to false}
{set stolen to false}

{if len(inventory) > 0:
    {for cargo in inventory:
        {set cargocarried to cargocarried + cargo.total}
        {set haulage to haulage + cargo.haulage}
        {if cargo.stolen > 0: {set stolen to true}}
        {if status.docked:
            {for prohibited in station.prohibited:
                {if cargo.name = prohibited: {set illegal to true}}
            }
        }
    }
}


{if cargocarried > 0:
    {if cargocarried = ship.cargocapacity:
        {OneOf("La bodega de carga está llena", "el compartimento de carga está lleno")}.
    |else:
        {Occasionally(2, "Ahora mismo")} {OneOf("llevamos", "transportamos")} 
    }

    {if len(inventory) = 1:
        {if inventory[0].name = "Drón":
            {if cargocarried = 1:un |else:{cargocarried}} drón{if cargocarried != 1:es}.
        |else:
           {if cargocarried = 1:una |else:{cargocarried}} tonelada{if cargocarried != 1:s} de {inventory[0].name}.
        }
    |else:
        {if cargocarried < ship.cargocapacity: de cargamento.}
        {OneOf("la siguiente mercancía","lo siguiente")}.
        {set position to 0}
        {for cargo in inventory:

            {if cargo.name = "Drón":
                 {if cargo.total = 1:un |else:{cargo.total}} drón{if cargo.total != 1:es}
            |else:
               {if cargo.total = 1:una |else:{cargo.total}} tonelada{if cargo.total != 1:s} de {cargo.name}
            }
            {set position to position + 1}
            {if position = len(inventory) - 1: , y |else:,}
        }
    }.

    {if cargocarried < ship.cargocapacity:
        {set free to ship.cargocapacity - cargocarried}
        Tienes {free} tonelada{if free != 1:s} {OneOf("","de espacio libre")}{if free != 1:s}.
    }

    {if stolen || illegal:
        Atención: Llevamos carga
        {if stolen: robada}
        {if illegal:
            {if stolen:, e }
            ilegal en esta jurisdicción
        }.
    }

|else:
    {OneOf("Tu bahía de carga está vacía.","Tu bodega de carga está vacía.","No tienes carga.","No llevas ninguna carga.")}
}

