FSD engaged.script:
{_ Context }
{SetState('eddi_context_last_subject', 'fsd')}
{SetState('eddi_context_last_action', 'engage')}
{SetState('eddi_context_system_name', event.system)}
{SetState('eddi_context_system_system', event.system)}
{SetState('eddi_context_fsd_target', event.target)}

{if event.target = 'Hyperspace':
    {_ Rewrite context to represent a jump }
    {SetState('eddi_context_last_subject', 'jump')}
    {SetState('eddi_context_last_action', 'start')}

    {_ set system to SystemDetails(event.system) _} 
    {_ linea comentada porque sino no traducia la variable system.allegiance... ¿¿porque?? _}

    {_ Wait for the FSD countdown and initial jump}
    {Pause(5000)}

    {OneOf("Destino confirmado","salto en progreso","telemetría obtenida")}.
    {ShipName()} está {OneOf("en dirección a", "en ruta a", "viajando a", "en tránsito a")} el sistema {P(event.system)}. 
{_ Se ha cambiado la variable system.name por event.system _}

    {if event.system = homesystem.name:
        Bienvenido a casa, {F("Honorific")}
    |else:
        {set scoopables to ["O", "B", "A", "F", "G", "K", "M"]}
        {if slice(event.stellarclass, 0, 1) = "D":
            Precaución: detectada enana blanca.
        |elif event.stellarclass = "N":
            Precaución: detectada estrella de neutrones.
        |elif event.stellarclass = "H":
            Precaución: detectado agujero negro.
        |elif find(scoopables, event.stellarclass) = -1:
            {OneOf("Atención:","Tenga en cuenta que","Cuidado Comandante,")} la estrella de destino no permite el repostaje de combustible.
        }

        {if lastsystem.allegiance && lastsystem.allegiance != "Ninguno" && lastsystem.allegiance != "Independiente" && (!system.allegiance || system.allegiance = "Ninguno" || system.allegiance = "Independente"):
            Comandante {OneOf("estamos abandonando el espacio de", "hemos dejado", "ya no estamos en territorio de")} {lastsystem.allegiance}.
        |elif lastsystem.allegiance && lastsystem.allegiance != system.allegiance && system.allegiance && system.allegiance != "Ninguno":
            {OneOf("ahora estamos en espacio de", "hemos llegado a", "estamos entrando en espacio de")} {system.allegiance}.
            {if cmdr.title != "Comandante":
                El Gobierno local le da la bienvenida {Oneof("como","con el título de")} {F("Honorific")}.
            }
        }

        {if system.visits = 0:
            Es su primera visita a este sistema
        |elif system.visits = 1:
            Es su segunda visita a este sistema
        |elif system.visits = 2:
            Es su tercera visita a este sistema
        |elif system.visits = 3:
            Es su cuarta visita a este sistema
        |else:
            Ha visitado este sistema {system.visits} veces
        }.

        {if !system.population:
            {OneOf("Este sistema está despoblado","No hay presencia humana en este sistema", "La Humanidad aún no ha colonizado este sistema")}.
        |else:
            {F("System report")}
        }

        {if system.distancefromhome:
            {Occasionally(7, "{OneOf(\"Se encuentra a\", \"Su actual posición es a\", \"Actualmente esta a\")} {Humanise(system.distancefromhome)} años luz de {OneOf(\"casa\", \"{P(homesystem.name)}\")}.")}
        }

        {if system.comment:
            Ha hecho {OneOf("una anotación", "un comentario", "una observación")} {OneOf("sobre", "acerca de", "en relación a")} este sistema que {OneOf("dice", "indica", "señala")} {system.comment}.
        }
    }
}

Fuel check.script:
{_ Fuel Check _}
{_ Report fuel level _}

{if ship.name = state.eddi_fuelcheck_shipname:
    {if state.eddi_context_fuel_used > state.eddi_fuelcheck_maxfuel:
        {SetState('eddi_fuelcheck_maxfuel', state.eddi_context_fuel_used)}
    }
|else:
    {SetState('eddi_fuelcheck_maxfuel', state.eddi_context_fuel_used)}
    {SetState('eddi_fuelcheck_shipname', ship.name)}
}

{set maxfuel to state.eddi_fuelcheck_maxfuel}
{set fuelremaining to state.eddi_context_fuel_remaining}
{set maxjump to fuelremaining/maxfuel}
{set currentfuel to round(fuelremaining / ship.fueltanktotalcapacity * 100, 0)}
{set shipsfuel to:
   {OneOf(" niveles", " depósito", " reservas")} de combustible {Occasionally(2, "de {P(ShipName())}" )}
}
{set currentpercent to: 
   al {currentfuel} por ciento {Occasionally(2, "de su capacidad")}
}
{set title to Occasionally(3,"{F('Honorific')}")}
{if maxjump < 0.5:
   Peligro!
   {shipsfuel} agotado!
   {Pause(500)}
   Se encuentra en la reserva. Desconecte todos los sistemas no esenciales!
   {Pause(500)}
   Transpondedor de emergencia listo-{title}
|elif maxjump < 1.25:
   {OneOf("Alerta", "Precaución", "Peligro", "Atención")}-{title}:
   {shipsfuel} {OneOf("peligrosamente bajo", "casi agotado")}.
   {OneOf("Por favor recargue combustible", "Se recomienda encarecidamente que recargue combustible")}
|elif currentfuel < 25:
   {OneOf("Alerta", "Precaución", "Peligro", "Atención")}-{title}:
   {shipsfuel} {OneOf("por debajo del 25%", "{currentpercent}" )}
|elif currentfuel < 50:
   {shipsfuel} {OneOf("por debajo del 50%", "{currentpercent}" )}-{title}
|elif currentfuel < 75:
   {Occasionally(2, "{shipsfuel} {currentpercent}-{title}")}
|elif currentfuel < 100:
   {Occasionally(3, "{shipsfuel} {currentpercent}-{title}")}
|else:
   {shipsfuel} {OneOf(currentpercent, "al máximo")}-{title}
}

