    {set material to MaterialDetails(material)}

    {_ Context }
    {SetState('eddi_context_last_subject', 'material')}
    {SetState('eddi_context_material_name', material.name)}
}

{if !material:
    {_ Fetch from context }
    {set material to MaterialDetails(state.eddi_context_material_name)}
}

{if material && material.name:
    {set uses to len(material.blueprints)}
    {if slice(material.name, len(material.name)-1) = 's':
        {set plural to 'son'}
    |else:
        {set plural to 'es'}
    }
    {if uses = 0:
      Ninguna cantidad de {material.name} {plural} de utilidad para ningún plano
    |elif uses = 1:
        {set name to cat(material.blueprints[0].name, material.blueprints[0].modulename, 'grade', material.blueprints[0].grade)}
        {material.name} {plural} de utilidad para el plano {name}
    |elif uses = 2:
        {set name0 to cat(material.blueprints[0].name, material.blueprints[0].modulename, 'grade', material.blueprints[0].grade)}
        {set name1 to cat(material.blueprints[1].name, material.blueprints[1].modulename, 'grade', material.blueprints[1].grade)}
        {material.name} {plural} de utilidad para los planos {name0} y {name1}
    |else:
        {material.name} {plural} de utilidad para los {len(material.blueprints)} planos siguientes:
        {set cur to 0}
        {while cur < len(material.blueprints):
            {set name to cat(material.blueprints[cur].name, material.blueprints[cur].modulename, 'grade', material.blueprints[cur].grade)}
            {if cur = 0:
                {name}
            |elif cur < len(material.blueprints) - 1:
                , {name}
            |else:
                , y {name}
            }
            {set cur to cur + 1}
        }
    }
|else:
    No consigo encontrar información sobre este material
}.

Materials discard report.script:
{_ Find those that are above maximum, desired and minimum}
{set shoulddiscardmaterials to []}
{set coulddiscardmaterials to []}
{set lastdiscardmaterials to []}

{for material in materials:
    {if material.maximum && material.amount > material.maximum:
      {set shoulddiscardmaterials to cat(shoulddiscardmaterials, [material])}
    |elif material.desired && material.amount > material.desired:
      {set coulddiscardmaterials to cat(coulddiscardmaterials, [material])}
    |elif material.minimum && material.amount > material.minimum:
      {set lastdiscardmaterials to cat(lastdiscardmaterials, [material])}
    }
}

{if len(shoulddiscardmaterials) > 0:
   Estamos por encima del nivel máximo de {if len(shoulddiscardmaterials) = 1: 1 material |else: {len(shoulddiscardmaterials)} materials}.  Podemos deshacernos de
    {if len(shoulddiscardmaterials) = 1:
        {shoulddiscardmaterials[0].amount - shoulddiscardmaterials[0].maximum} {shoulddiscardmaterials[0].material}
    |elif len(shoulddiscardmaterials) = 2:
        {shoulddiscardmaterials[0].amount - shoulddiscardmaterials[0].maximum} {shoulddiscardmaterials[0].material} y {shoulddiscardmaterials[1].amount - shoulddiscardmaterials[1].maximum} {shoulddiscardmaterials[1].material}
    |else:
        {set cur to 0}
        {while cur < len(shoulddiscardmaterials):
            {if cur = 0:
                {shoulddiscardmaterials[cur].amount - shoulddiscardmaterials[cur].maximum} {shoulddiscardmaterials[cur].material}
            |elif cur < len(shoulddiscardmaterials) - 1:
                , {shoulddiscardmaterials[cur].amount - shoulddiscardmaterials[cur].maximum} {shoulddiscardmaterials[cur].material}
            |else:
                , y {shoulddiscardmaterials[cur].amount - shoulddiscardmaterials[cur].maximum} {shoulddiscardmaterials[cur].material}
            }
            {set cur to cur + 1}
        }
    }.
|elif len(coulddiscardmaterials) > 0:
    Tenemos más cantidad de {if len(coulddiscardmaterials) = 1: 1 material |else: {len(coulddiscardmaterials)} materials} de lo deseado.  Podemos descartar
    {if len(coulddiscardmaterials) = 1:
        {coulddiscardmaterials[0].amount - coulddiscardmaterials[0].desired} {coulddiscardmaterials[0].material}
    |elif len(coulddiscardmaterials) = 2:
        {coulddiscardmaterials[0].amount - coulddiscardmaterials[0].desired} {coulddiscardmaterials[0].material} y {coulddiscardmaterials[1].amount - coulddiscardmaterials[1].desired} {coulddiscardmaterials[1].material}
    |else:
        {set cur to 0}
        {while cur < len(coulddiscardmaterials):
            {if cur = 0:
                {coulddiscardmaterials[cur].amount - coulddiscardmaterials[cur].desired} {coulddiscardmaterials[cur].material}
            |elif cur < len(coulddiscardmaterials) - 1:
                , {coulddiscardmaterials[cur].amount - coulddiscardmaterials[cur].desired} {coulddiscardmaterials[cur].material}
            |else:
                , y {coulddiscardmaterials[cur].amount - coulddiscardmaterials[cur].desired} {coulddiscardmaterials[cur].material}
            }
            {set cur to cur + 1}
        }
    }.
|elif len(lastdiscardmaterials) > 0:
    Estamos por encima del mínimo requerido de {if len(lastdiscardmaterials) = 1: 1 material |else: {len(lastdiscardmaterials)} materials}.  Podemos eliminar 
    {if len(lastdiscardmaterials) = 1:
        {lastdiscardmaterials[0].amount - lastdiscardmaterials[0].minimum} {lastdiscardmaterials[0].material}
    |elif len(lastdiscardmaterials) = 2:
        {lastdiscardmaterials[0].amount - lastdiscardmaterials[0].minimum} {lastdiscardmaterials[0].material} y {lastdiscardmaterials[1].amount - lastdiscardmaterials[1].minimum} {lastdiscardmaterials[1].material}
    |else:
        {set cur to 0}
        {while cur < len(lastdiscardmaterials):
            {if cur = 0:
                {lastdiscardmaterials[cur].amount - lastdiscardmaterials[cur].minimum} {lastdiscardmaterials[cur].material}
            |elif cur < len(lastdiscardmaterials) - 1:
                , {lastdiscardmaterials[cur].amount - lastdiscardmaterials[cur].minimum} {lastdiscardmaterials[cur].material}
            |else:
                , y {lastdiscardmaterials[cur].amount - lastdiscardmaterials[cur].minimum} {lastdiscardmaterials[cur].material}
            }
            {set cur to cur + 1}
        }
    }.
|else:
    No podemos deshacernos de ningún material si queremos estar dentro de los mínimos necesarios
}

