    |elif reportbody.stellarclass = "CS":
        una estrella de carbono
    |elif find(["C", "CN", "CH", "CHd"], reportbody.stellarclass) > -1:
        una estrella de carbono{Occasionally(2, ", de tipo tardío")}{Occasionally(4, ", con una atmósfera dominada por el carbono")}
    |elif reportbody.stellarclass = "CJ":
        una estrella de carbono{Occasionally(2, ", de tipo tardío")}{Occasionally(2, ", con altos niveles de carbono 13 en su atmósfera")}
    |elif reportbody.stellarclass = "MS":
        una estrella de tipo tardío{Occasionally(4, ", que antes fue una Estrella Clase M, y ahora está en camino de convertirse en una de carbono")}
    |elif reportbody.stellarclass = "S":
        una estrella de tipo tardío{Occasionally(4, ", que antes fue una Estrella Clase M, y que ahora se está convirtiendo ya en una de carbono")}
    |elif reportbody.stellarclass = "D":
        una enana blanca
    |elif reportbody.stellarclass = "DA":
        una enana blanca{Occasionally(4, " con una atmósfera rica en hidrógeno")}
    |elif reportbody.stellarclass = "DAB":
        una enana blanca{Occasionally(4, " con una atmósfera rica en hidrógeno y helio")}
    |elif slice(reportbody.stellarclass, 0, 1) = "D":
        una enana blanca
    |elif slice(reportbody.stellarclass, 0, 1) = "N":
        una estrella de neutrones {Occasionally(4, " las cuales tienen un peligroso núcleo de alta densidad")}
    |elif reportbody.stellarclass = "H":
        un agujero negro
    |elif reportbody.stellarclass = "X":
        una entidad estelar muy exótica...
    |elif reportbody.stellarclass = "SuperMassiveBlackHole":
        un agujero negro supermasivo
    |elif reportbody.stellarclass = "A_BlueWhiteSuperGiant":
        una supergigante blanco-azulada{Occasionally(4, ", que se está expandiendo hacia convertirse en una supergigante roja")}
    |elif reportbody.stellarclass = "F_WhiteSuperGiant":
        una supergigante amarilla {Occasionally(4, ", que se está expandiendo hacia convertirse en una supergigante roja")}
    |elif reportbody.stellarclass = "M_RedSuperGiant":
        una supergigante roja{Occasionally(4, ", en su fase de combustión de helio")}
    |elif reportbody.stellarclass = "M_RedGiant":
        una gigante roja {Occasionally(4, ", que se está expandiendo hacia convertirse en supergigante")}
    |elif reportbody.stellarclass = "K_OrangeGiant":
        una gigante naranja {Occasionally(4, ", que se expande mientras se dirige hacia el final de su vida")}
    |elif reportbody.stellarclass = "RoguePlanet":
        un planeta {OneOf("interestelar","vagabundo","errante","huérfano")}
    |elif reportbody.stellarclass = "Nebula":
        una nebulosa
    |elif reportbody.stellarclass = "StellarRemnantNebula":
        un{OneOf("a nebulosa remanente"," remanente de Supernova")}
    |else:
        una estrella clase {Spacialise(reportbody.stellarclass)}
    }

    {set ageInYears to reportbody.age * 1000000}
