        }

        {if reportbody.tempprobability < 0.01:
            {set notables to cat(notables, [cat("una increiblemente baja temperatura de superficie de ", Humanise(reportbody.temperature), " grados kelvin")])}
        |elif reportbody.tempprobability < 0.1:
            {set notables to cat(notables, [cat("una extremadamente baja temperatura de superficie de ", Humanise(reportbody.temperature), " grados kelvin")])}
        |elif reportbody.tempprobability < 1:
