        |elif reportbody.tempprobability > 99.99:
            {set notables to cat(notables, [cat("una extrema alta temperatura de superficie de ", Humanise(reportbody.temperature), " grados kelvin")])}
        |elif reportbody.tempprobability > 99.9:
            {set notables to cat(notables, [cat("una temperatura de superficie increiblemente alta de ", Humanise(reportbody.temperature), " grados kelvin")])}
        |elif reportbody.tempprobability > 99:
            {set notables to cat(notables, ["una inusual alta temperatura de superficie"])}
        |elif reportbody.tempprobability > 90:
            {set notables to cat(notables, ["una temperatura de superficie mayor que la media"])}
        }

        {if len(notables) = 0:
            {OneOf("", "Esta es un claro ejemplo de esta clase de estrellas.", "No hay nada destacable en este estrella.", "de su clase, está dentro de la media.")}
        |else:
            {OneOf('Comparado con otras estrellas de su clase', 'En comparación con otras estrellas de clase  {reportbody.stellarclass}', 'Si la comparamos con otras estrellas de clase {reportbody.stellarclass}')}, esta estrella es
