{_ Crew promotion }
{_ Triggered when crewmember combat rank increases }


Crew member {event.name} has been promoted to the combat rank of {event.combatrating}.

Crime check station.script:
{_ Crime check station }
{_ Check claims, fines & bounties related to the current station }


{set CheckPad(current_station) to:
    {set largest_pad to lcase(current_station.largestpad)}
    {if largest_pad != "none":
        {if largest_pad = "large": {return true} }
        |elif largest_pad = "medium:
            {if lcase(ship.size) = "large": {return false}
            |else: {return true}
        }
        {if lcase(ship.size) = "small": {return true}
    }
    {return false}
}

{set HasService(service) to:
    {if CheckPad(current_station):
        {if find(current_station.stationservices, service) > -1:
            {return true}
        |else:
            {return false}
        }
    |else: {return false}
    }
}

{set current_station to StationDetails(state.eddi_context_body_name)}

{set factions_claims to []}
{set system_claims to 0}
{set factions_fines to []}
{set system_fines to 0}
{set factions_bounties to []}
{set station_report to false}

{for record in criminalrecord:
    {if record.bounties > 0:
        {set factions_bounties to cat(factions_bounties, [record.faction])}
    }
    {for faction in system.factions:
        {if record.faction = faction.name:
            {if record.claims > 0:
                {set factions_claims to cat(factions_claims, [record.faction])}
                {set system_claims to system_claims + record.claims}
            }
            {if record.fines > 0:
                {set factions_fines to cat(factions_fines, [record.faction])}
                {set system_fines to system_fines + record.fines}
            }
        }
    }
}

{set claim_count to len(factions_claims)}
{set fine_count to len(factions_fines)}
{set bounty_count to len(factions_bounties)}

{if bounties > 0 && HasService("Interstellar Factors Contact"):
    Information: There are legal facilities at this station to clear your pending
    {if bounty_count = 1: bounty |else: bounties}.
    {set station_report to true}
}

{if (system_fines > 0 || system_claims > 0) && !station_report:
    {if HasService("Contacts"):
        Information: You may clear your pending
         {if fine_count = 1: fine |else: fines} at this station.
        {set station_report to true}
    }
}

{if system_claims > 0 && !station_report:
    {if HasService("Contacts"):
        Information: You may claim your pending
        {if claim_count = 1: reward |else: rewards} at this station.
    }
}

Crime check system.script:
{_ Crime check system }
{_ Check claims, fines & bounties related to the current system }


{set CheckPad(station) to:
    {set largest_pad to lcase(station.largestpad)}
    {if largest_pad != "none":
        {if largest_pad = "large": {return true} }
        |elif largest_pad = "medium:
            {if lcase(ship.size) = "large": {return false}
            |else: {return true}
        }
        {if lcase(ship.size) = "small": {return true}
    }
    {return false}
}

{set NearestStation(service) to:
    {set distance to 0}
    {set station_count to 0}
    {set orbital_count to 0}

    {_ If preferred & available, use orbital stations }
    {if orbitalpriority:
        {for station in system.orbitalstations:
            {if find(station.stationservices, service) > -1:
                {set orbital_count to orbital_count + 1}
            }
        }
    }

    {_ Force orbital stations when game version is not 'Horizons' }
    {if orbital_count > 0 || !horizons:
        {set stations to system.orbitalstations}
    |else:
        {set stations to system.stations}
    }

    {_ Get the total count and nearest station }
    {for station in stations:
        {if CheckPad(station):
            {if find(station.stationservices, service) > -1:
                {set station_count to station_count + 1}
                {if distance = 0 || station.distancefromstar < distance:
                    {set distance to station.distancefromstar}
                    {set nearest_station to station.name}
                }
            }
        }
    }
}

{set factions_claims to []}
{set factions_fines to []}
{set factions_bounties to []}
{set station_report to false}
{set system_claims to 0}
{set system_fines to 0}
{set system_bounties to 0}
{set wanted_count to 0}

{for record in criminalrecord:
    {for faction in system.factions:
        {if record.faction = faction.name:

            {if record.claims > 0:
                {set factions_claims to cat(factions_claims, [record.faction])}
                {set system_claims to system_claims + record.claims}
            }

            {set reports to record.finesIncurred}
            {if len(reports) > 0:
                {for report in reports:
                    {if report.shipId = ship.LocalId:
                        {set system_fines to system_fines + report.amount}
                        {if find(factions_fines, record.faction) < 0:
                            {set factions_fines to cat(factions_fines, [record.faction])}
                            {if find(factions_bounties, record.faction) < 0:
                                {set wanted_count to wanted_count + 1}
                            }
                        }
                    }
                }
            }

            {set reports to record.bountiesIncurred}
            {if len(reports) > 0:
                {for report in reports:
                    {if report.shipId = ship.LocalId:
                        {set system_bounties to system_bounties + report.amount}
                        {if find(factions_bounties, record.faction) < 0:
                            {set factions_bounties to cat(factions_bounties, [record.faction])}
                            {if find(factions_fines, record.faction) < 0:
                                {set wanted_count to wanted_count + 1}
                            }
                        }
                    }
                }
            }
        }
    }
}

{set claim_count to len(factions_claims)}
{set fine_count to len(factions_fines)}
{set bounty_count to len(factions_bounties)}

{if wanted_count > 0:
    Warning: {ShipName()} is wanted in this system by {wanted_count}
    {if wanted_count = 1: faction |else: factions},
    for total fines and bounties of {Humanise(system_fines + system_bounties)} credits.
}

{_ Report nearest station to clear bounties }
{if system_bounties > 0:
    {NearestStation("Interstellar Factors Contact")}
    {if station_count > 0:
        There are legal facilities at {station_count} {if orbitalpriority: orbital}
        {if station_count = 1: station |else: stations} to clear your pending
        {if bounty_count = 1: bounty |else: bounties}.
        {if station_count = 1: It |else: The nearest} is {nearest_station},
        {round(distance, 1)} light-seconds from the main star.
        {set station_report to true}
    }
}

{if system_fines > 0 && !station_report:
    {NearestStation("Contacts")}
    {if station_count > 0:
        There {if station_count = 1: is |else: are} {station_count} {if orbitalpriority: orbital}
        {if station_count = 1: station |else: stations} to clear your pending
        {if fine_count = 1: fine |else: fines}.
        {if station_count = 1: It |else: The nearest} is {nearest_station},
        {round(distance, 1)} light-seconds from the main star.
        {set station_report to true}
    }
}

{_ Report nearest station for pending claims }
{if system_claims > 0:
    You have bond and bounty claims in this system for {claim_count}
    {if claim_count = 1: faction |else: factions}, for a total of {Humanise(system_claims)} credits.

    {if !station_report:
        {if system_fines = 0: {NearestStation("Contacts")}}
        {if station_count > 0:
            There {if station_count = 1: is |else: are} {station_count} {if orbitalpriority: orbital}
            {if station_count = 1: station |else: stations} to claim your
            {if claim_count = 1: reward |else: rewards}.
            {if station_count = 1: It |else: The nearest} is {nearest_station},
            {round(distance, 1)} light-seconds from the main star.
        }
    }
}

Crime report.script:
{_ Crime report}
{_ Report on all pending claims, fines, and bounties }


{set factions_claims to []}
{set claims_count to 0}
{set factions_fines to []}
{set fines_count to 0}
{set factions_bounties to []}
{set systems_bounties to []}

{for record in criminalrecord:
    {set faction to FactionDetails(record.faction)}
    {if faction:
        {if record.bounties > 0:
            {set factions_bounties to cat(factions_bounties, [record.faction])}
            {for presence in faction.presences:
                {if find(presence.systemName, systems_bounties) = -1:
                    {set systems_bounties to cat(systems_bounties, [presence.systemName])}
                }
            }
        }
        {if record.fines > 0:
            {set factions_fines to cat(factions_fines, [record.faction])}
        }
        {if record.claims > 0:
            {set factions_claims to cat(factions_claims, [record.faction])}
        }

        {for report in record.factionReports:
            {if !report.bounty && report.crimeEDName != "none":
                {set fines_count to fines_count + 1}
            }
            {if report.crimeEDName = "none":
                {set claims_count to claims_count + 1}
            }
        }
    }
}

{set factions_count to len(factions_bounties)}
{set systems_count to len(factions_bounties)}

{if factions_count > 0:
    You are wanted by {factions_count} {if factions_count = 1: faction |else: factions}
    in {systems_count} {if systems_count = 1: system |else: systems} for a total of
    {Humanise(bounties)} credits.
}

{set factions_count to len(factions_fines)}
{if factions_count > 0:
    You have {fines_count} pending {if fines_count = 1: fine |else: fines} from
    {factions_count} {if factions_count = 1: faction |else: factions},
    for a total of {Humanise(fines)} credits.
}

{set factions_count to len(factions_claims)}
{if factions_count > 0:
    You have {claims_count} uncollected {if claims_count = 1: claim |else: claims} from
    {factions_count} {if factions_count = 1: faction |else: factions},
    for a total of {Humanise(claims)} credits.
}

