Died.script:
{if len(event.ships) > 0:

    {set commanders to []}
    {for cmdr in event.commanders:
        {if find(cmdr, "$ShipName") >= 0:
           {_ Ignore NPC ships _}        
        |else: 
           {set commanders to cat(commanders, [cmdr])}
        }
    }

    {ShipName()} vient
    {Occasionally(2, " juste ")}
    {OneOf(" d'être détruit", " d'être broyé", "d'être explosé", "de se faire bouffer", "de se prendre une raclée")}

    {if len(commanders) = 1:
        par un {event.ships[0]} 
  piloté par  {commanders[0]} de rang   {event.ratings[0]}.
    |elif len(commanders) = 2:
        par un {event.ships[0]} 
  piloté par  {commanders[0]} de rang   {event.ratings[0]},
        et par un {event.ships[1]} 
  piloté par  {commanders[1]} de rang   {event.ratings[1]}.
    |elif len(commanders) > 2:
        {set cur to 0}
        by
        {while cur < len(commanders):
            {if cur = 0:
                par un {event.ships[cur]} 
          piloté par  {commanders[cur]} de rang   {event.ratings[cur]}
            |elif cur < len(commanders) - 1:
                , un {event.ships[cur]} 
          piloté par  {commanders[cur]} de rang   {event.ratings[cur]}
            |else:
                , et un {event.ships[cur]} 
          piloté par  {commanders[cur]} de rang   {event.ratings[cur]}
            }
            {set cur to cur + 1}
        }
    }
}

Discovery scan.script:
{_ Fetch from Context _}
{set reportSystem to SystemDetails(state.eddi_context_system_name)}

{if !reportSystem.name || reportSystem.name = "":

    I'm not sure which system you are asking about.

|else:

    {if ship.Role.edname = "Exploration" || ship.Role.edname = "Multipurpose":
        {if reportSystem:
            {if len(reportSystem.bodies) > 0 && len(reportSystem.bodies) < event.totalbodies:

                {Pause(1000)}

                {set scannedBodies to 0}
                {for body in reportSystem.bodies:
                    {if body.scanned:
                        {set scannedBodies to scannedBodies + 1}
                    }
                }
                {set bodiesToScan to (event.totalbodies- scannedBodies)}

                {if scannedBodies < event.totalbodies:
                    {OneOf(
                        "{scannedBodies} out of {event.totalbodies} bodies have been scanned", 
                        "{bodiesToScan} {if bodiesToScan > 1: bodies remain |else: body remains} to be scanned"
                    )}
                    {Occasionally(3, OneOf("here", "in this system"))}.
                }

            |else:

                {set bodiesToMap to F("Bodies to map")}
                {if bodiesToMap:
                    {bodiesToMap}
                }
            }
        }
    }
}

