AFMU repairs.script:
{event.item} 
{if event.repairedfully:
    fully repaired
|else:
    partially repaired 
    {Occasionally(2, cat(
        OneOf("to ", "at "),
        Humanise(event.health * 100),
        " percent functionality"
    ))}
}

{Occasionally(2, 
    cat(OneOf(", ", "and is"), " ready for re-activation")
)}.

Blueprint make report.script:
{_ Fetch from context / state _}
{set blueprintname to state.blueprint}
{set blueprintgrade to state.blueprintgrade}

{if blueprintname && blueprintgrade:
    {_ Fetch full blueprint details }
    {set blueprint to BlueprintDetails(blueprintname, blueprintgrade)}

    {_ Context }
    {SetState('eddi_context_last_subject', 'blueprint')}
    {SetState('eddi_context_blueprint_name', blueprintname)}
    {SetState('eddi_context_blueprint_grade', blueprintgrade)}
}

{if !blueprint:
    {_ Fetch from context }
    {set blueprint to BlueprintDetails(state.eddi_context_blueprint_name, state.eddi_context_blueprint_grade)}
}

{_ Function to obtain current inventory of a material}
{set _inventory(required) to:
    {for material in materials:
        {if material.material = required:
            {return material}
        }
    }
}

{set canbemade to 999}

{if blueprint:
    {_ Find the smallest number of this blueprint that can be made }
    
    {set cur to 0}
    {while cur < len(blueprint.materials):
        {set inventory to _inventory(blueprint.materials[cur].material)}
        {if !inventory:
            {set canbemade to 0}
        |else:
            {if inventory.amount < canbemade:
                {set canbemade to inventory.amount}
            }
        }
        {set cur to cur + 1}
    }

    {if canbemade = 0:
        You cannot make any {blueprintname} grade {blueprintgrade}
    |elif canbemade = 1:
        You can make one {blueprintname} grade {blueprintgrade}
    |else:
        You can make {blueprintname} grade {blueprintgrade} {Humanise(canbemade)} times.
    }
    
|else:
    No information available on materials required for {blueprintname} grade {blueprintgrade}
}.

Blueprint material report.script:
{_ Fetch from context / state _}
{set blueprintname to state.blueprint}
{set blueprintgrade to state.blueprintgrade}

{if blueprintname && blueprintgrade:
    {_ Fetch full blueprint details }
    {set blueprint to BlueprintDetails(blueprintname, blueprintgrade)}

    {_ Context }
    {SetState('eddi_context_last_subject', 'blueprint')}
    {SetState('eddi_context_blueprint_name', blueprintname)}
    {SetState('eddi_context_blueprint_grade', blueprintgrade)}
}

{if blueprintname:
    {_ Fetch full blueprint details }
    {set blueprint to BlueprintDetails(blueprintname, blueprintgrade)}

    {_ Context }
    {SetState('eddi_context_last_subject', 'blueprint')}
    {SetState('eddi_context_blueprint_name', blueprintname)}
    {SetState('eddi_context_blueprint_grade', blueprintgrade)}
}

{if !blueprint || !blueprintgrade:
    {_ Fetch from context }
    {set blueprint to BlueprintDetails(state.eddi_context_blueprint_name, state.eddi_context_blueprint_grade)}
}

{if blueprint && blueprintgrade:
    {set materials to len(blueprint.materials)}
    {blueprintname} grade {blueprintgrade} requires 
    {if materials = 1:
        {blueprint.materials[0].material}
    |elif materials = 2:
        {blueprint.materials[0].material} and {blueprint.materials[1].material}
    |else:
        {set cur to 0}
        {while cur < len(blueprint.materials):
            {if cur = 0:
                {blueprint.materials[cur].material}
            |elif cur < len(blueprint.materials) - 1:
                , {blueprint.materials[cur].material}
            |else:
                , and {blueprint.materials[cur].material}
            }
            {set cur to cur + 1}
        }
    }
|else:
    No information available on materials required for {blueprintname} grade {blueprintgrade}
}.

Bodies mapped.script:
{_ Fetch from Context, if a context is set _}
{if state.eddi_context_system_name:
    {set reportSystem to SystemDetails(state.eddi_context_system_name)}
|else:
    {set reportSystem to SystemDetails(system.systemname)}
}

{_ Build a list of scanned bodies _}

{set mappedList to []}
{for body in reportSystem.bodies:
   {if body.mapped:
       {set mappedList to cat(mappedList, [P(body.shortname, "body")])} 
   }
}

{_ Begin speaking _}

{if len(mappedList) > 0:
    {if len(mappedList) = 1: Body |else: Bodies}
    {List(mappedList)}
    {if len(mappedList) = 1: has |else: have }
    {set already to Occasionally(2, "already")}
    {OneOf("{already} been mapped", "been mapped {already}")}.
|else:
    You have yet to map any bodies in this system.
}

Bodies to map.script:
{_ Preferences: _}

{set reportTerraformables to true}   {_ Reports all terraformable and terraformed bodies _}
{set highValueThreshold to 80000}   {_ The minimum threshold for estimated exploration value worth mapping _}
{set reportPlanetTypes to [
    "Ammonia world": true,
    "Earth-like world": true,
    "Gas giant with ammonia based life": false,
    "Gas giant with water based life": false,
    "Helium gas giant": false,
    "Helium-rich gas giant": false,
    "High metal content world": false,
    "Icy body": false,
    "Metal-rich body": false,
    "Rocky body": false,
    "Rocky ice world": false,
    "Class I gas giant": false,
    "Class II gas giant": false,
    "Class III gas giant": false,
    "Class IV gas giant": false,
    "Class V gas giant": false,
    "Water giant": false,
    "Water giant with life": false,
    "Water world": true,
]}

{_ Fetch from Context, if a context is set _}
{if state.eddi_context_system_name:
    {set reportSystem to SystemDetails(state.eddi_context_system_name)}
|else:
    {set reportSystem to SystemDetails(system.systemname)}
}

{_ Identify bodies worth mapping _}

{set toMapList to []}
{set mappedBodies to 0}
{set totalBodies to 0}

{for body in reportSystem.bodies:
    {set totalBodies to totalBodies + 1}
    {if body.mapped:
        {set mappedBodies to mappedBodies + 1}
    |else:
        {if body.bodyType.invariantName != "Star":
            {if body.terraformState.invariantName = "Candidate for terraforming" 
             || body.terraformState.invariantName = "Terraformed":
                {if reportTerraformables:
                    {if !toMapList[body.shortname]: 
                        {set toMapList to cat(toMapList, [body.shortname])}
                    } 
                }
            |elif reportPlanetTypes[body.planetClass.invariantName]:
                {if !toMapList[body.shortname]: 
                    {set toMapList to cat(toMapList, [body.shortname])}
                }
            |elif body.estimatedvalue > highValueThreshold:
                {if !toMapList[body.shortname]: 
                    {set toMapList to cat(toMapList, [body.shortname])}
                } 
            }
        }
    }
}
{set toMapCount to len(toMapList)}

{_ Begin speaking _}

{if toMapCount > 0:

    {if toMapCount = 1: Body |else: Bodies}

    {set cur to 0}
    {while cur < toMapCount:
        {P(toMapList[cur], "body")}
        {set cur to cur + 1}
        {if cur = toMapCount - 1: and
        |elif cur < toMapCount - 1:,
        }
    }

    {if toMapCount = 1: is |else: are }
    {if event.type = "Body mapped": "also" }
    {if toMapCount = 1: a }
    {OneOf("recommended", "good")}
    {set candidates to when(toMapCount = 1, "candidate", "candidates")}
    {OneOf('{candidates} for mapping', '{candidates} to map', 'mapping {candidates}')}.

|elif mappedBodies > 0:

    {OneOf(
        "You have mapped all {if mappedBodies < totalBodies: {OneOf(\"worthwhile\", \"interesting\")}} bodies.",
        "All {OneOf( \"{if mappedBodies < totalBodies: worthwhile} bodies\", \"bodies of interest\" )} {OneOf( \"have been mapped.\", \"are now mapped.\" )}"
    )}
}

Body atmosphere report.script:
{_ Preferences _}
{set reportAtmoPressure to 0}   {_ Set to 1 if you want to hear about the atmospheric pressure. _}
{set reportAtmoTempScale to 0}  {_ Set to 0 if you want to hear about temperatures in Kelvin _}
                                {_ Set to 1 if you want to hear about temperatures in Celsius _}
                                {_ Set to 2 if you want to hear about temperatures in Fahrenheit _}
                                {_ Set to 3 if you don't want to hear about the temperature of the body _}

{_ Define functions for converting temperature scales _}
{set ToCelsius(kelvin) to: {round(kelvin - 273)}}
{set ToFahrenheit(kelvin) to: {round((kelvin-273)*9/5 + 32)}}
{set TempCalc(kelvin) to: 
    {if reportAtmoTempScale = 0: {round(kelvin)}}
    {if reportAtmoTempScale = 1: {ToCelsius(kelvin)}}
    {if reportAtmoTempScale = 2: {ToFahrenheit(kelvin)}}
}

{_ Name our temperature scale _}
{if reportAtmoTempScale = 0: {set tempscale to: Kelvin}}
{if reportAtmoTempScale = 1: {set tempscale to: degrees Celsius}}
{if reportAtmoTempScale = 2: {set tempscale to: degrees Fahrenheit}}

{_ Fetch from context _}
{set reportbody to BodyDetails(state.eddi_context_body_name, state.eddi_context_body_system)}
{if !reportbody: 
    {set reportbody to BodyDetails(state.eddi_context_body_name)}
}

{if !reportbody.name || reportbody.name = "":
    I'm not sure which body you are asking about.

|else:

    {if find(reportbody.planettype, "giant") >= 0:
        {if find(reportbody.planettype, "Class") >= 0: 
            {set sudarsky to Occasionally(3, "Sud-dar-ski")}
        }
        {set planetdesc to OneOf("{sudarsky} Gas giants like this one have", "This {sudarsky} gas giant has")}
        {if find(reportbody.planettype, "Class V") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(1373)}}
                temperatures above {Humanise(tempmin)} {tempscale}, {OneOf("and", "with", "forming")}
            }
            a dense cloud layer of {OneOf("vaporized silicates and iron", "silicate and iron vapors")}.
        |elif find(reportbody.planettype, "Class IV") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(354)}}
                {set tempmax to: {TempCalc(903)}}
                temperatures {OneOf("of", "ranging")} between {Humanise(tempmin)} and {Humanise(tempmax)} {tempscale}, {OneOf("and", "with", "forming")}
            }
            a dense cloud layer of {OneOf("alkali metals vapors", "vaporized alkali metals")}.   
        |elif find(reportbody.planettype, "Class III") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(251)}}
                {set tempmax to: {TempCalc(353)}}
                surface temperatures {OneOf("of", "ranging")} between {Humanise(tempmin)} and {Humanise(tempmax)} {tempscale},
            }
            a temperature at which {OneOf("no gaseous elements will form any clouds", "suitable chemicals are not present in the atmosphere to form a sustained cloud layer", "the atmosphere cannot sustain a persistent cloud layer")}.
        |elif find(reportbody.planettype, "Class II") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(154)}}
                {set tempmax to: {TempCalc(250)}}
                surface temperatures {OneOf("of", "ranging")} between {Humanise(tempmin)} and {Humanise(tempmax)} {tempscale}, {OneOf("and", "with", "forming")}
            }
            a dense cloud layer of water vapors.  
        |elif find(reportbody.planettype, "Class I") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(153)}}
                {set tempmax to: {TempCalc(83)}}
                surface temperatures of {Humanise(tempmax)} {tempscale} {Occasionally(2, OneOf(", or less,", ", at most,"))} {OneOf("and", "with", "forming")}
             }
             a dense cloud layer of ammonia vapor.  
        }

    |elif reportbody.atmosphere = "No atmosphere" || !reportbody.atmosphere:
        {Occasionally(4, OneOf("There is no atmosphere.", "It is devoid of any atmosphere.", "The years have scoured it clean of any atmosphere."))}
        {if reportbody.temperature && reportAtmoTempScale < 3:
            The surface temperature averages
            {TempCalc(reportbody.temperature)} {tempscale}.
        }

    |elif reportbody.pressure: 
        {if reportbody.pressure < 0.1:
            {set pressdesc to OneOf("trace gas", "super thin")}
        |elif reportbody.pressure < 0.25:
            {set pressdesc to "very thin"}
        |elif reportbody.pressure < 0.5:
            {set pressdesc to "thin"}
        |elif reportbody.pressure < 2:
            {set pressdesc to ""}
        |elif reportbody.pressure < 10:
            {set pressdesc to "thick"}
        |elif reportbody.pressure < 50:
            {set pressdesc to OneOf("dense", "very thick")}
        |elif reportbody.pressure < 100:
            {set pressdesc to OneOf("very dense", "super thick")}
        |elif reportbody.pressure >= 100:
            {set pressdesc to OneOf("crushing", "super dense")}
        }
        {if find(reportbody.atmosphere, "-rich") >= 0:
            {set element to token(reportbody.atmosphere, "-rich", "")}
            {set desc to OneOf("I detect a ", "There is a ", "Sensors detect a")}
            {OneOf("{desc} {pressdesc} {reportbody.atmosphere} atmosphere", "The {pressdesc} atmosphere is {reportbody.atmosphere}", "The {pressdesc} atmosphere is rich in {element}")}
        |elif find(reportbody.atmosphere, "vapour") >= 0:
            {OneOf("The {pressdesc} atmosphere is composed of {reportbody.atmosphere}s", "The {pressdesc} atmosphere is rich in {reportbody.atmosphere}s", "The {pressdesc} atmosphere contains high levels of {reportbody.atmosphere}s")}
        |elif reportbody.planetClass.invariantName = "Earth-like world":
            The atmosphere is similar to Earth's, 
        |else:
            {set mostly to OneOf("primarily", "mainly", "mostly")}
            {OneOf("The {pressdesc} atmosphere is composed {mostly} of", "Most prevalent in the {pressdesc} atmosphere is")}
            {reportbody.atmosphere}
        }
        { if reportAtmoPressure > 0:
            with a surface pressure of {Humanise(reportbody.pressure)} Earth atmospheres
        }
        {if reportbody.temperature && reportAtmoTempScale < 3:
            {set temp to: {TempCalc(reportbody.temperature)}}
            {if find(temp, "-") >= 0: 
                {set temp to cat(temp*-1, tempscale, "below zero")}
            |else:
                {set temp to cat(temp, tempscale)}
            }
            with an average surface temperature of {temp}
        }.
    }
}

Body mapped.script:
{_ Preferences _}

{set bodyReport to 2}             {_ 0 = No body report, 1 = Summary body report, 2 = Body report _}
{set recommendOtherMapping to 1}  {_ 0 or 1. If 1, recommends other bodies from the `Bodies to map` script _}

{_ Context}
{SetState('eddi_context_last_action', 'scan')}
{SetState('eddi_context_last_subject', 'body')}
{SetState('eddi_context_body_system', system.name)}
{SetState('eddi_context_body_name', event.name)}
{SetState('eddi_context_last_scan_estimated_value', event.estimatedvalue)}

{_ Process the event _}

{if ship.Role.edname = "Exploration" || ship.Role.edname = "Multipurpose":

    {Pause(3000)}

    {OneOf("Mapping", "{Occasionally(2, \"Surface\")} Map")}
    {OneOf("is", "is now")}
    {OneOf("complete", "finished")}.

    {if event.probesused <= event.efficiencytarget:
        Efficiency bonus {OneOf("awarded", "granted")}.
    }

    {Pause(500)}
    {if bodyReport = 2:
        {F("Body report")}
    |elif bodyReport = 1:
        {F("Body report summary")}
    }

    {if recommendOtherMapping: 
        {Pause(1500)}
        {F("Bodies to map")}
    }
}

Body materials report.script:
{_ Preferences _}
{set reportMats to 1} {_ Set to 0 if you want to hear about materials below your minimum inventory level _}
                      {_ Set to 1 if you want to hear about materials below your desired inventory level _}
                      {_ Set to 2 if you want to hear about all materials irrespective of inventory levels _}

{set reportMatConc to 1} {_ Set to 0 if you only want to hear about great material concentrations _}
                         {_ Set to 1 if you want to hear about good and great material concentrations _}
                         {_ Set to 2 if you want to hear about all material concentrations _}

{set reportMatPercent to 1} {_ Set to 0 if you do not want to hear about material percentages _}
                            {_ Set to 1 if you only want to hear about great material percentages _}
                            {_ Set to 2 if you want to hear about good and great material percentages _}
                            {_ Set to 3 if you want to hear about all material percentages _}

{_ Fetch from context _}
{set reportbody to BodyDetails(state.eddi_context_body_name, state.eddi_context_body_system)}
{if !reportbody: 
    {set reportbody to BodyDetails(state.eddi_context_body_name)}
}

{if !reportbody.name || reportbody.name = "":
    {_ If it's still not possible to get the body name...  _}
    Which body are you
    {OneOf("asking about","referring to")}?

|else:

    {_ Define a function to obtain a material from inventory}
    {set _inventory(required) to:
        {for material in materials:
            {if material.material = required:
                {return material}
            }
        }
    }

    {if reportbody.landable:

        {set goodmaterials to []}
        {set greatmaterials to []}
        {set othermaterials to []}

        {for material in reportbody.materials:
            {set inventoryMat to _inventory(material.material)}
            {if (reportMats >= 0 && inventoryMat.amount < inventoryMat.minimum && inventoryMat.minimum > 0)
             || (reportMats >= 1 && inventoryMat.amount < inventoryMat.desired && inventoryMat.desired > 0)
             || (reportMats >= 2):
                {if material.percentage >= material.definition.greatpctbody && reportMatConc > -1:
                    {set greatmaterials to cat(greatmaterials, [material])}
                |elif material.percentage >= material.definition.goodpctbody && reportMatConc > 0:
                    {set goodmaterials to cat(goodmaterials, [material])}
                |elif reportMatConc > 1:
                    {set othermaterials to cat(othermaterials, [material])}
                }
            }
        }

        {_ Start speaking _}

        {if len(goodmaterials) > 0 || len(greatmaterials) > 0 || len(othermaterials) > 0:
            {if reportbody.name = reportbody.shortname:
                {P(reportbody.shortname, "body")}
            |else:
                {OneOf("This body", cat("Body ", P(reportbody.shortname, "body")))} 
            }
            {OneOf("contains", "has")}
        }

        {if len(greatmaterials) > 0:
            {OneOf("very high levels", "very large deposits")} of
            {set cur to 0}
            {set length to len(greatmaterials) - 1}
            {while cur < len(greatmaterials):
                {greatmaterials[cur].definition.name}
                {if reportMatPercent > 0: at {round(greatmaterials[cur].percentage, 1)}% }
                {set cur to cur + 1}
                {if cur = length: and
                |elif cur < length:,
                }
            }
  
            {if len(goodmaterials) > 0 && len(othermaterials) = 0:
                and
            |elif len(goodmaterials) > 0 || len(othermaterials) > 0:
                ,
            }
        }

        {if len(goodmaterials) > 0:
            {OneOf("high levels", "large deposits")} of
            {set cur to 0}
            {set length to len(goodmaterials) - 1}
            {while cur < len(goodmaterials):
                {goodmaterials[cur].definition.name}
                {if reportMatPercent > 1: at {round(goodmaterials[cur].percentage, 1)}% }
                {set cur to cur + 1}
                {if cur = length: and
                |elif cur < length:,
                }
            }
        }
   
        {if (len(greatmaterials) > 0 || len(goodmaterials) > 0) && len(othermaterials) > 0:
            and
        }
  
        {if len(othermaterials) > 0:
            {OneOf("moderate levels", "modest deposits")} of
            {set cur to 0}
            {set length to len(othermaterials) - 1}
            {while cur < len(othermaterials):
                {othermaterials[cur].definition.name}
                {if reportMatPercent > 2: at {round(othermaterials[cur].percentage, 0)}% }
                {set cur to cur + 1}
                {if cur = length: and
                |elif cur < length:,
                }
            }
        }
   
        {if len(goodmaterials) > 0 || len(greatmaterials) > 0 || len(othermaterials) > 0:
            .
        }
    }
}

Body report.script:
{_ Preferences _}
{set reportGravity to 1}        {_ Set to 0 if you don't want to hear about the body's gravity. _} 
{set reportTerraformState to 1} {_ Set to 0 if you don't want to hear about the terraform state of the body. _}
{set reportRings to 1}          {_ Set to 0 if you don't want to hear about the body's rings. _}
{set reportRotation to 0}       {_ Set to 1 if you want to hear about the body's rotation. _}
{set reportVolcanism to 1}      {_ Set to 0 if you don't want to hear about the body's volcanism. _}
{set reportAtmosphere to 1}     {_ Set to 0 if you don't want to hear about the body's atmosphere. _}
{set reportMaterials to 1}      {_ Set to 0 if you don't want to hear about the body's materials. _}
{set reportScanValue to 0}      {_ Set to 1 if you want to hear about the body's estimated value. _}

{_ Fetch from context _}
{set reportbody to BodyDetails(state.eddi_context_body_name, state.eddi_context_body_system)}
{if !reportbody: 
    {set reportbody to BodyDetails(state.eddi_context_body_name)}
}

{if !reportbody.name || reportbody.name = "":
    I'm not sure which body you are asking about.
|else:
    {if reportbody.shortname != reportbody.name: 
        {if reportbody.bodytype:
            {reportbody.bodytype}
        |else:
            {OneOf("Body", "Object")} 
        }
    }
    {P(reportbody.shortname, "body")} is

    {if reportGravity > 0:
        {if reportbody.gravity < 0.5:
            a low-gravity
        |elif reportbody.gravity < 2:
            a medium-gravity
        |elif reportbody.gravity < 4:
            a high-gravity
        |else:
            an extremely high-gravity
        }
    |else:
        a
    }

    {if reportTerraformState > 0:
        {if reportbody.terraformState.invariantName = "Candidate for terraforming":
            terraformable
        |elif reportbody.terraformState.invariantName = "Terraformed":
            terraformed
        }
    }

    {if find(reportbody.planettype, "Class") >= 0 && find(reportbody.planettype, "gas giant") >= 0:
        {set sudarsky to Occasionally(2, "Sud-dar-ski")}
        {if find(reportbody.planettype, "Class V") >= 0:
            {Occasionally(2, "{Sudarsky} class five")}
        |elif find(reportbody.planettype, "Class IV") >= 0:
            {Occasionally(2, "{Sudarsky} class four")}
        |elif find(reportbody.planettype, "Class III") >= 0:
            {Occasionally(2, "{Sudarsky} class three")}
        |elif find(reportbody.planettype, "Class II") >= 0:
            {Occasionally(2, "{Sudarsky} class two")}
        |elif find(reportbody.planettype, "Class I") >= 0:
            {Occasionally(2, "{Sudarsky} class one")}
        }
        gas giant
    |elif reportbody.planettype:
        {P(reportbody.planettype, "planettype")}
    |else: 
        {if reportbody.bodytype:
            {reportbody.bodytype}
        |else:
            world
        }
    }

    {if len(reportbody.rings) && reportRings > 0:
        with {if len(reportbody.rings) = 1: a}
        {if find(reportbody.reserves, "Pristine") >= 0:
            pristine
        |elif find(reportbody.reserves, "Major") >= 0:
            rich
        }
        {set ringname to []}
        {set ringcomposition to []}
        {for Ring in reportbody.rings:
            {if find(ringcomposition, Ring.composition) < 0:
                {set ringcomposition to cat(ringcomposition, [Ring.composition])}
            }
        }
        {if len(ringcomposition) > 0:
            {List(ringcomposition)}
        }
        {if find(ringname[0], "Belt") >= 0: 
            {set ring to "belt"}
        |else:
            {set ring to "ring"}
        }
        {ring}{if len(reportbody.rings) > 1 || len(event.rings) > 1:s}
    }

    {if reportbody.terraformState.invariantName = "Terraforming" && reportTerraformState > 0:
      in the process of being terraformed
    }

    {if reportbody.landable:
        that is suitable for landing
    }
    . {_ note the  full stop}

    {if reportbody.rotationalperiod && reportRotation > 0:
        {Pause(100)}
        It has a
        {if reportbody.rotationalperiod < -20.0:
            slow retrograde
        |elif reportbody.rotationalperiod < -0.5:
            retrograde
        |elif reportbody.rotationalperiod < 0.0:
            fast retrograde
        |elif reportbody.rotationalperiod <= 0.5:
            fast
        |elif reportbody.rotationalperiod > 20.0:
            slow
        }
        rotational period of {Humanise(round(reportbody.rotationalperiod, 1))} days
        {if reportbody.tidallylocked:
           and is tidally-locked
        }.
    }

    {if reportVolcanism: {F("Body volcanism report")} }

    {if reportAtmosphere: {F("Body atmosphere report")} }

    {if reportMaterials: {F("Body materials report")} }

    {if reportScanValue: {F("Report last scan value")} }
}

Body report summary.script:
{_ Preferences _}

{set reportTerraformables to 1}         {_ Reports terraformable and terraformed bodies _}
{set reportPlanetTypes to 1}            {_ Reports Earth-like and water worlds _}
{set reportMass to 1}                   {_ Reports notable masses (relative to Earth's) _}
{set reportRadius to 1}                 {_ Reports notable radii (in kilometers) _}
{set reportDensity to 0}                {_ Reports notable densities (in kilograms per cubic meter) _}
{set reportGravity to 1}                {_ Reports notable gravity (in G's) for landable bodies _}
{set reportTemperature to 1}            {_ Reports notable temperatures (in Kelvin) _}
{set reportMaterials to 1}              {_ Reports materials per preferences in the `Body materials` script. _}
{set reportLandableVolcanism to 0}      {_ Reports volcanism on landable worlds _}
{set reportRings to 1}                  {_ Reports ring details _}
{set reportInterestingOrbits to 1}      {_ Reports notable orbital characteristics _}
{set reportRapidRotation to 1}          {_ Reports very fast rotating bodies _}

{_ Fetch from context _}
{set reportbody to BodyDetails(state.eddi_context_body_name, state.eddi_context_body_system)}
{if !reportbody: 
    {set reportbody to BodyDetails(state.eddi_context_body_name)}
}

{_ Begin processing _}

{if !reportbody.name || reportbody.name = "":
    {_ If it's still not possible to get the body name...  _}
    Which body are you
    {OneOf("asking about","referring to")}?

|else:

    {set notables to []}

    {if reportInterestingOrbits:

        {set orbitNotables to []}

        {if reportbody.eccentricityprobability:
            {if reportbody.eccentricityprobability > 99.9:
                {set orbitNotables to cat(orbitNotables, [cat("incredibly ", OneOf("elliptical", "oblong", "elongated"))])}
            |elif reportbody.eccentricityprobability > 99:
                {set orbitNotables to cat(orbitNotables, [cat("extremely ", OneOf("elliptical", "oblong", "elongated"))])}
            |elif reportbody.eccentricityprobability > 97:
                {set orbitNotables to cat(orbitNotables, [cat("unusually ", OneOf("elliptical", "oblong", "elongated"))])}
            }
        }

        {if reportbody.inclinationprobability:
            {if reportbody.inclinationprobability > 99.9:
                {set orbitNotables to cat(orbitNotables, [cat("incredibly inclined")])}
            |elif reportbody.inclinationprobability > 99:
                {set orbitNotables to cat(orbitNotables, [cat("extremely inclined")])}
            |elif reportbody.inclinationprobability > 97:
                {set orbitNotables to cat(orbitNotables, [cat("unusually inclined")])}
            }
        }

        {if reportbody.orbitalperiodprobability:
            {if reportbody.orbitalperiod < 0: {set retrograde to cat(" ", "retrograde", " ")} }
            {if reportbody.orbitalperiodprobability < 0.1:
                {set orbitNotables to cat(orbitNotables, [cat("incredibly fast", retrograde)])}
            |elif reportbody.orbitalperiodprobability < 1:
                {set orbitNotables to cat(orbitNotables, [cat("extremely fast", retrograde)])}
            |elif reportbody.orbitalperiodprobability < 3:
                {set orbitNotables to cat(orbitNotables, [cat("unusually fast", retrograde)])}
            }
        }

        {if len(orbitNotables) > 0:
            {if StartsWithVowel(orbitNotables[0]): {set a to "an"} |else: {set a to "a"} }
            {set cur to 0}
            {set length to len(orbitNotables) - 1}
            {while cur < len(orbitNotables):
                {set orbitdescription to cat(orbitdescription, orbitNotables[cur])}
                {set cur to cur + 1}
                {if cur = length: 
                    {set orbitdescription to cat(orbitdescription, " and ")}
                |elif cur < length:
                    {set orbitdescription to cat(orbitdescription, ", ")}
                }
            }
            {set notables to cat(notables, [cat(a, " ", orbitdescription, " ", "orbit")])}
        }
    }

    {if reportbody.rotationalperiod < 0: {set retrograde to cat(" ", "retrograde", " ")} }
    {if reportRapidRotation && reportbody.rotationalperiodprobability:
        {if !retrograde && reportbody.rotationalperiodprobability < 0.1:
            {set notables to cat(notables, [cat("an incredibly short rotational period of ", Humanise(reportbody.rotationalperiod), " days")])}
        |elif !retrograde && reportbody.rotationalperiodprobability < 1:
            {set notables to cat(notables, [cat("an extremely short rotational period of ", Humanise(reportbody.rotationalperiod), " days")])}
        |elif !retrograde && reportbody.rotationalperiodprobability < 3:
            {set notables to cat(notables, [cat("an unusually short rotational period of ", Humanise(reportbody.rotationalperiod), " days")])}
        }
    }

    {if reportMass && reportbody.massprobability:
        {if reportbody.massprobability < 0.1:
            {set notables to cat(notables, [cat("an incredibly low mass at ", Humanise(reportbody.earthmass), " times Earth's")])}
        |elif reportbody.massprobability < 1:
            {set notables to cat(notables, [cat("an extremely low mass at ", Humanise(reportbody.earthmass), " times Earth's")])}
        |elif reportbody.massprobability < 3:
            {set notables to cat(notables, [cat("an unusually low mass at ", Humanise(reportbody.earthmass), " times Earth's ")])}
        |elif reportbody.massprobability > 99.9:
            {set notables to cat(notables, [cat("an incredibly high mass at ", Humanise(reportbody.earthmass), " times Earth's")])}
        |elif reportbody.massprobability > 99:
            {set notables to cat(notables, [cat("an extremely high mass at ", Humanise(reportbody.earthmass), " times Earth's")])}
        |elif reportbody.massprobability > 97:
            {set notables to cat(notables, [cat("an unusually high mass at ", Humanise(reportbody.earthmass), " times Earth's")])}
        }
    }

    {if reportRadius && reportbody.radiusprobability:
        {if reportbody.radiusprobability < 0.1:
            {set notables to cat(notables, [cat("an incredibly low radius of ", Humanise(reportbody.radius), " kilometers")])}
        |elif reportbody.radiusprobability < 1:
            {set notables to cat(notables, [cat("an extremely low radius of ", Humanise(reportbody.radius), " kilometers")])}
        |elif reportbody.radiusprobability < 3:
            {set notables to cat(notables, [cat("an unusually low radius of ", Humanise(reportbody.radius), " kilometers")])}
        |elif reportbody.radiusprobability > 99.9:
            {set notables to cat(notables, [cat("an extremely high radius of ", Humanise(reportbody.radius), " kilometers")])}
        |elif reportbody.radiusprobability > 99:
            {set notables to cat(notables, [cat("an incredibly large radius of ", Humanise(reportbody.radius), " kilometers")])}
        |elif reportbody.radiusprobability > 97:
            {set notables to cat(notables, [cat("an unusually high radius of ", Humanise(reportbody.radius), " kilometers")])}
        }
    }

    {if reportTemperature && reportbody.tempprobability:
        {if reportbody.tempprobability < 0.1:
            {set notables to cat(notables, [cat("an incredibly low surface temperature of ", Humanise(reportbody.temperature), " Kelvin")])}
        |elif reportbody.tempprobability < 1:
            {set notables to cat(notables, [cat("an extremely low surface temperature of ", Humanise(reportbody.temperature), " Kelvin")])}
        |elif reportbody.tempprobability < 3:
            {set notables to cat(notables, [cat("an unusually low surface temperature of ", Humanise(reportbody.temperature), " Kelvin")])}
        |elif reportbody.tempprobability > 99.9:
            {set notables to cat(notables, [cat("an extremely high surface temperature of ", Humanise(reportbody.temperature), " Kelvin")])}
        |elif reportbody.tempprobability > 99:
            {set notables to cat(notables, [cat("an incredibly high surface temperature of ", Humanise(reportbody.temperature), " Kelvin")])}
        |elif reportbody.tempprobability > 97:
            {set notables to cat(notables, [cat("an unusually high surface temperature of ", Humanise(reportbody.temperature), " Kelvin")])}
        }
    }

    {if reportGravity && reportbody.landable && reportbody.gravityprobability:
        {if reportbody.gravityprobability < 0.1:
            {set notables to cat(notables, [cat("an incredibly low gravity of ", Humanise(reportbody.gravity), " times Earth's")])}
        |elif reportbody.gravityprobability < 1:
            {set notables to cat(notables, [cat("an extremely low gravity of ", Humanise(reportbody.gravity), " times Earth's")])}
        |elif reportbody.gravityprobability < 3:
            {set notables to cat(notables, [cat("an unusually low gravity of ", Humanise(reportbody.gravity), " times Earth's")])}
        |elif reportbody.gravityprobability > 99.9:
            {set notables to cat(notables, [cat("an incredibly high gravity of ", Humanise(reportbody.gravity), " times Earth's")])}
        |elif reportbody.gravityprobability > 99:
            {set notables to cat(notables, [cat("an extremely high gravity of ", Humanise(reportbody.gravity), " times Earth's")])}
        |elif reportbody.gravityprobability > 97:
            {set notables to cat(notables, [cat("an unusually high gravity of ", Humanise(reportbody.gravity), " times Earth's")])}
        }
    }

    {if reportDensity && reportbody.densityprobability:
        {if reportbody.densityprobability < 0.1:
            {set notables to cat(notables, [cat("an incredibly low density of ", Humanise(reportbody.density), " kilograms per cubic meter")])}
        |elif reportbody.densityprobability < 1:
            {set notables to cat(notables, [cat("an extremely low density of ", Humanise(reportbody.density), " kilograms per cubic meter")])}
        |elif reportbody.densityprobability < 3:
            {set notables to cat(notables, [cat("an unusually low density of ", Humanise(reportbody.density), " kilograms per cubic meter")])}
        |elif reportbody.densityprobability > 99.9:
            {set notables to cat(notables, [cat("an incredibly high density of ", Humanise(reportbody.density), " kilograms per cubic meter")])}
        |elif reportbody.densityprobability > 99:
            {set notables to cat(notables, [cat("an extremely high density of ", Humanise(reportbody.density), " kilograms per cubic meter")])}
        |elif reportbody.densityprobability > 97:
            {set notables to cat(notables, [cat("an unusually high density of ", Humanise(reportbody.density), " kilograms per cubic meter")])}
        }
    }

    {if reportVolcanism:
        {if reportbody.volcanism && reportbody.landable:
            {set notables to cat(notables, [cat(OneOf("volcanic", "tectonic"), " activity")])}
        }
    }

    {if reportRings:
        {if reportbody.rings:
            {set ringcompositions to []}
            {set cur to 0}
            {for ring in reportbody.rings:
                {if find(ring.name, "Ring") > -1:
                    {if find(ringcompositions, ring.composition) = -1:
                        {set ringcompositions to cat(ringcompositions, [cat(ring.composition)])}
                    }
                }
                {set cur to cur + 1}
            }
            {set cur to 0}
            {set length to len(ringcompositions) - 1}
            {while cur < len(ringcompositions):
                {set ringsdescription to cat(ringsdescription, ringcompositions[cur])}
                {set cur to cur + 1}
                {if cur = length: 
                    {set ringsdescription to cat(ringsdescription, " and ")}
                |elif cur < length:
                    {set ringsdecription to cat(ringsdescription, ", ")}
                }
            }
            {if ringsdescription:
                {if len(ringcompositions) = 1:
                    {if StartsWithVowel(reportbody.reserves): 
                        {set a to "an "} 
                    |else: 
                        {set a to "a "}
                    }
                    {set rings to "ring"}
                |else:
                    {set rings to "rings"} 
                }
                {if reportbody.reserveLevel.invariantName != "None": 
                    {set reserves to reportbody.reserves}
                }
                {set notables to cat(notables, [cat(a, " ", reserves, " ", ringsdescription, " ", rings)])}
            }
        }
    }

    {if reportTerraformables:
        {if reportbody.terraformState.invariantName = "Candidate for terraforming":
            {set notables to cat(notables, [OneOf("potential for terraforming", "terraformable conditions")])}
        |elif reportbody.terraformState.invariantName = "Terraformed":
            {set notables to cat(notables, ["terraformed conditions"])}
        }
    }

    {if reportPlanetTypes:
        {if reportbody.planetClass.invariantName = "Earth-like world":
            {set notables to cat(notables, ["Earth-like conditions"])}
        |elif reportbody.planetClass.invariantName = "Water world":
            {set notables to cat(notables, [OneOf("abundant liquid water", "abundant liquid water at the surface")])}
        }
    }

    {_ Begin speaking _}

    {if len(notables) > 0:
        {if reportbody.shortname != reportbody.bodyname:
            {if reportbody.bodytype:
                {reportbody.bodytype}
            |else:
                Body
            }
            {P(reportbody.shortname, "body")}
        |else:
            {P(reportbody.bodyname, "body")}
        }
        is

        {if len(notables) = 1:
            {set desc to "notable"}
        |elif len(notables) = 2:
            {set desc to OneOf('interesting', 'notable', 'remarkable')}
        |elif len(notables) = 3:
            {set desc to OneOf('exceptional', 'remarkable')}
        |elif len(notables) = 4:
            {set desc to OneOf('extraordinary')}
        |elif len(notables) >= 5:
            {set desc to OneOf('one of a kind', 'unique')}
        }
        {OneOf("{desc},", "{if StartsWithVowel(desc): an |else: a } {desc} {P(reportbody.planettype, 'planettype')}")}

        with {List(notables)}.
    }

    {if reportMaterials:
        {F("Body materials report")}
    }
}

