Cargo depot.script:
{_ Cargo depot }
{_ Triggered when engaging with a mission depot }


{if event.updatetype = "Collect":
    You have collected {event.amount} tonne{if event.amount != 1:s} of {event.commodity} from the mission depot.
|elif event.updatetype = "Deliver":
    You have delivered {event.amount} tonne{if event.amount != 1:s} of {event.commodity} to the mission depot.

    {Pause(500)}
    {set remaining to event.totaltodeliver - event.delivered}
    {set haulage to HaulageDetails(event.missionid)}

    {if find(haulage.name, "Wing") > -1:
        Wing
    |elif find(haulage.name, "Rank") > -1:
        Naval
    |else:
        Solo
    }

    {if remaining = 0:
        mission requirements are now completed.
    |else:
        mission is now {round(event.delivered / event.totaltodeliver * 100, 0)} percent complete.
    }
}

Cargo report.script:
{_ Cargo report }
{_ Report current cargo manifest }

{set cargocarried to 0}
{set haulage to 0}
{set illegal to false}
{set stolen to false}

{if len(inventory) > 0:
    {for cargo in inventory:
        {set cargocarried to cargocarried + cargo.total}
        {set haulage to haulage + cargo.haulage}
        {if cargo.stolen > 0: {set stolen to true}}
        {if status.docked:
            {for prohibited in station.prohibited:
                {if cargo.name = prohibited.name: {set illegal to true}}
            }
        }
    }
}

{if cargocarried > 0:
    {if cargocarried = ship.cargocapacity:
        Your cargo {OneOf("bay", "hold")} is full
    |else:
        You are carrying {cargocarried} tonne{if cargocarried != 1:s}
    }

    {if len(inventory) = 1:
        {if inventory[0].name = "Limpet":
            of limpets.
        |else:
            of {inventory[0].name}.
            {if haulage = cargocarried:
                Note: All cargo is mission related haulage.
            }
        }
    |else:
        {if cargocarried < ship.cargocapacity: of cargo}.
        {Occasionally(2, "Cargo")} {OneOf("Manifest", "Inventory")} is as follows:

        {set c to []}
        {for cargo in inventory:
            {if cargo.total > 0:
                {set c to cat(c, [cargo])}
            }
        }

        {set position to 0}
        {for cargo in c:
            {cargo.total} tonne{if cargo.total != 1:s} of 
            {if cargo.name = "Limpet":
                limpets
            |else:
                {cargo.name}
            }
            {set position to position + 1}
            {if position = len(c) - 1: and |else:,}
        }
        {if haulage > 0:
            Note: {haulage} tonne{if haulage != 1:s} of your manifest is mission related haulage.
        }
    }.

    {if cargocarried < ship.cargocapacity:
        {set free to ship.cargocapacity - cargocarried}
        You {OneOf("have {free} tonne{if free != 1:s} free",
            "have {free} tonne{if free != 1:s} of free space",
            "can carry an additional {free} tonne{if free != 1:s}")}.
    }

    {if stolen || illegal:
        Warning: You are carrying
        {if stolen: stolen goods}
        {if illegal:
            {if stolen:, and }
            goods that are illegal in this jurisdiction
        }.
    }

|else:
    {OneOf("Your cargo bay is empty.","You have no cargo.","You are carrying no cargo.")}
}

