    {OneOf("Jump in progress","Entering portal")} to the {P(reportsystem.systemname, "starsystem")} system
    {if len(event.shortname) > 0:
        {set reportBody to BodyDetails(event.bodyname, event.systemname)} 
        near
        {if len(reportBody.bodytype) > 0: {reportBody.bodytype} |else: body}
        {P(event.shortname, "body")}
    }.

    {if reportsystem.systemname = homesystem.systemname:
        Welcome home, {F("Honorific")}
    |else:       
        {if lastsystem.allegiance && lastsystem.allegiance != "None" && lastsystem.allegiance != "Independent" && (!reportsystem.allegiance || reportsystem.allegiance = "None" || reportsystem.allegiance = "Independent"):
            You {OneOf("are leaving", "have left", "are no longer in")} {lastsystem.allegiance} space.
        |elif lastsystem.allegiance && lastsystem.allegiance != reportsystem.allegiance && system.allegiance && system.allegiance != "None":
            You {OneOf("are now in", "have entered", "are entering")} {reportsystem.allegiance} space.
            {if cmdr.title != "Commander":
                Welcome back {F("Honorific")}.
            }
        }

        {if reportsystem.visits = 0:
            This is your first visit to this system.
        |elif reportsystem.visits = 1:
            This is your second visit to this system.
        |elif reportsystem.visits = 2:
            {Occasionally(2, "This is your third visit to this system.")}
        |elif reportsystem.visits = 3:
            {Occasionally(2, "This is your fourth visit to this system.")}
        |else:
            {Occasionally(3, "You have visited this system {reportsystem.visits} times.")}
        }

        {if reportsystem.population && reportsystem.population > 0:
            {if SecondsSince(reportsystem.lastVisitSeconds) > 3600:
                {F("System report")}
            }
        |else:
            {OneOf("This system is not populated","There is no human presence here", "Humans have yet to colonise this system")}.
        }

        {if reportsystem.distancefromhome:
            {set ReportDistanceFromHome() to:
                {return cat(
                	OneOf("You are", "Current location is", "You are now"), " ",
	                Humanise(reportsystem.distancefromhome), " lightyears from ",
	                OneOf("home", "{P(homesystem.name, "starsystem")}"), "."
	            )}
            }
            {Occasionally(7, ReportDistanceFromHome())}
        }

        {if reportsystem.comment:
            You made a {OneOf("note", "comment", "remark")} {OneOf("about", "for", "on")} this system.  It {OneOf("is as follows", "says", "reads", "is")} {reportsystem.comment}.
        }
    }
|else:
    Fleet carrier arriving at the {P(event.systemname, "starsystem")} system
    {if len(event.shortname) > 0:
        {set reportBody to BodyDetails(event.bodyname, event.systemname)} 
        near
        {if len(reportBody.bodytype) > 0: {reportBody.bodytype} |else: body}
        {P(event.shortname, "body")}
    }.
}

