Entered normal space.script:
{_ Entered normal space }
{_ Triggered when your ship enters normal space }


{_ Context }
{SetState('eddi_context_last_subject', 'fsd')}
{SetState('eddi_context_last_action', 'disengage')}
{SetState('eddi_context_system_name', event.system)}
{SetState('eddi_context_body_name', event.body)}
{SetState('eddi_context_body_type', event.bodytype)}
{SetState('eddi_context_body_system', event.system)}

{set station_notifications to:
    {set station_missions to F("Mission check station")}
    {set station_crimes to F("Crime check station")}
    {if station_missions || station_crimes:
        {_ Pause for station ATC before speaking again _}
        {Pause(5000)}
        Information:
        {if station_missions:
            {Pause(500)}
            {station_missions}
        }
        {if station_crimes:
            {Pause(500)}
            {station_crimes}
        }
    }
}

{Pause(500)}

{OneOf("{ShipName()} has", "")} 
{OneOf("left supercruise", "{OneOf(\"entered\", \"returned to\", \"dropped to\")} normal space")}

{if event.bodytype_invariant = 'Planet':
    {OneOf("near", "close to", "in the vicinity of")}
    {set time to SecondsSince(0) - state.eddi_context_approach_time}
    {if time < 60:
        {set settlement to state.eddi_context_last_settlement}
        {set reportStation to StationDetails(settlement, event.system)}
        {if reportStation:
            {SetState('eddi_context_body_name', settlement)}
            {if reportStation.Model.invariantName != "Unknown Station":
                {P(reportStation.model, "station")},
            } 
            {P(settlement, "station")}
            {station_notifications}
        |else:
            {P(settlement, "station")}
        }
    |else:
        {set reportBody to BodyDetails(event.body, event.system)}
        {reportBody.bodytype)} {P(reportBody.name, "body")}
    }
|elif event.bodytype_invariant = 'Star':
    {set reportBody to BodyDetails(event.body, event.system)}
    {OneOf("near", "close to", "in the vicinity of")} {reportBody.bodytype)} {P(reportBody.name, "body")}
|elif event.bodytype_invariant = 'Station':
    {set reportStation to StationDetails(event.body, event.system)}
    {OneOf("near", "close to", "in the vicinity of")}
    {P(reportStation.model, "station")}, {P(event.body, "body")}
    {station_notifications}
|elif event.bodytype_invariant = 'Planetary Ring':
    {event.bodytype} {P(token(event.body," Ring",0), "body")}
}.

Entered signal source.script:
{set warninglevel to:
   {set entered to OneOf("Entered", "Dropped into", "Entering")}
   {if event.threat < 2:
      {entered}
   |elif event.threat = 2:
      Caution: {entered} medium threat
   |elif event.threat = 3:
      Warning: {entered} high-threat
   |elif event.threat = 4:
      Danger: {entered} very high-threat
   |elif event.threat = 5:
      Extreme Danger: {Occasionally(2, entered)} very high-threat
   |elif event.threat = 6:
      Extreme Danger: {Occasionally(2, entered)} extremely high-threat
   |elif event.threat = 7:
      Extreme Danger: {Occasionally(2, entered)} ultra high-threat
   |else:
      Extreme Danger: {Occasionally(2, entered)} Unprecedented level {event.threat} threat
   }
}

{set source to token(event.source, "$USS_Type_", 1)}

{if source = "Aftermath;":
   {set source to OneOf("combat aftermath", "dissipating combat")}
|elif ((source = "Anomaly;" || source = "Salvage;") && event.threat = 2):
   {set warninglevel to entered}
   {set source to OneOf("alien", "xeno", "non-human", "Thargoid probe")}
|elif (source = "Anomaly;" && event.threat = 4):
   {set warninglevel to entered}
   {set source to OneOf("alien", "xeno", "non-human", "Thargoid sensor")}
|elif source = "Anomaly;":
   {set source to OneOf("anomalous", "unknown", "mysterious")}
|elif source = "Ceremonial;":
    {set source to "ceremonial comms"}
|elif source = "Convoy;":
    {set source to "convoy"}
|elif source = "DisruptedWakeEchoes;":
    {set source to ""}
|elif source = "DistressSignal;":
   {set source to "distress"}
|elif source = "MissionTarget;":
   {set source to OneOf("mission", "mission encoded")}
|elif source = "NonHuman;":
   {set source to OneOf("alien", "xeno", "non-human")}
|elif source = "Salvage;":
    {set source to "degraded emissions"}
|elif source = "ValuableSalvage;":
    {set source to "encoded emissions"}
|elif source = "VeryValuableSalvage;":
    {set source to "high grade emissions"}
|elif source = "WeaponsFire;":
   {set source to OneOf("combat", "weapons fire")}
}

{Pause(2000)}
{warninglevel} {source} signal source. 

{if system.state = "Anarchy" && source = "degraded emissions" && system.population > 0: 
   {Occasionally(2, "
      Caution:
      {set local to OneOf('local', 'nearby')}
      {set chatter to OneOf('chatter', 'gossip', 'rumor')}
      {set indicate to OneOf('claims', 'suggests', 'implies', 'reports')}
      {OneOf('{local} transmissions {indicate}', 
         '{chatter} in {local} transmissions {indicate}')}
      {Occasionally(3, 'that there is')}
      {OneOf('increased', 'elevated', 'heightened')}
      {OneOf('criminal {Occasionally(2, \\'syndicate\\')}', 
             'pirate {Occasionally(2, OneOf(\\'gang\\', \\'clan\\'))}' )}
      {OneOf('activity', 'presence')}
      in {OneOf('the area', 'the vicinity', 'this sector', 'this system')}.
      {OneOf('Remain', 'Stay', 'Keep')} {OneOf('vigilant', 'alert', 'frosty', 'sharp')} 
      {Occasionally(3,' {F(\\'Honorific\\')}' )}.
   ")}
}

