{SetState('eddi_context_system_system', event.system)}
{SetState('eddi_context_fsd_target', event.target)}
{SetState('eddi_context_system_lastvisit', system.lastVisitSeconds)}

{_ Zero the 'remaining jump' context to only use values following this event _}
{SetState('eddi_context_remaining_jumps', 0)}

{if event.target = 'Hyperspace':
    {_ Rewrite context to represent a jump }
    {SetState('eddi_context_last_subject', 'jump')}
    {SetState('eddi_context_last_action', 'start')}

    {set reportsystem to SystemDetails(event.system)}

    {_ Wait for the FSD countdown and initial jump}
    {Pause(5000)}

    {OneOf("Destination confirmed","Jump in progress","Telemetry obtained")}.
    {ShipName()} is {OneOf("heading to", "on route to", "travelling to", "in transit to")} the {P(reportsystem.name, "starsystem")} system.
    {if reportsystem.name = homesystem.name:
        Welcome home, {F("Honorific")}
    |else:
        {set scoopables to ["O", "B", "A", "F", "G", "K", "M"]}
        {if slice(event.stellarclass, 0, 1) = "D":
            Caution: white dwarf detected.
        |elif event.stellarclass = "N":
            Caution: neutron star detected.
        |elif event.stellarclass = "H":
            Caution: black hole detected.
        |elif find(scoopables, event.stellarclass) = -1:
            {Occasionally(2, "Information: ")}
            Arrival star is not scoopable.
        }
        
        {if lastsystem.allegiance && lastsystem.allegiance != "None" && lastsystem.allegiance != "Independent" && (!reportsystem.allegiance || reportsystem.allegiance = "None" || reportsystem.allegiance = "Independent"):
            You {OneOf("are leaving", "have left", "are no longer in")} {lastsystem.allegiance} space.
        |elif lastsystem.allegiance && lastsystem.allegiance != reportsystem.allegiance && system.allegiance && system.allegiance != "None":
            You {OneOf("are now in", "have entered", "are entering")} {reportsystem.allegiance} space.
            {if cmdr.title != "Commander":
                Welcome back {F("Honorific")}.
            }
        }

        {if reportsystem.visits = 0:
            This is your first visit to this system.
        |elif reportsystem.visits = 1:
            This is your second visit to this system.
        |elif reportsystem.visits = 2:
            {Occasionally(2, "This is your third visit to this system.")}
        |elif reportsystem.visits = 3:
            {Occasionally(2, "This is your fourth visit to this system.")}
        |else:
            {Occasionally(3, "You have visited this system {reportsystem.visits} times.")}
        }

        {if !reportsystem.population && lastsystem.population:
            {OneOf("This system is not populated","There is no human presence here", "Humans have yet to colonise this system")}.
        |elif reportsystem.population:
            {if SecondsSince(reportsystem.lastVisitSeconds) > 3600:
                {F("System report")}
            }
        }

        {if reportsystem.distancefromhome:
            {set ReportDistanceFromHome() to:
            	{return cat(
            	    OneOf("You are", "Current location is", "You are now"), " ",
	                Humanise(reportsystem.distancefromhome), " lightyears from ",
	                OneOf("home", "{P(homesystem.name, 'starsystem')}"), "."
	            )}
            } 
            {Occasionally(7, ReportDistanceFromHome())}
        }

        {if reportsystem.comment:
            You made a {OneOf("note", "comment", "remark")} {OneOf("about", "for", "on")} this system.  It {OneOf("is as follows", "says", "reads", "is")} {reportsystem.comment}.
        }
    }
}

Fuel check.script:
{_ Fuel Check _}
{_ Report fuel level _}

{_ Might be a taxi or multicrew session. We ignore those. We only want this when we're in our own ship. }
{if vehicle = "Ship":

    {for compartment in ship.compartments:
        {if compartment.module.basename = "FuelScoop":    
        {set scoop_present to true}
        }
    }

    {set shipsfuel to:
        Fuel {OneOf("levels", "tanks", "reserves")}
        {Occasionally(2, "are")} {Occasionally(2, "now")}
    }

    {set currentfuel to round(status.fuel_percent, 0)}
    {set currentpercent to:
        at {currentfuel} percent {Occasionally(2, "capacity")}
    }

    {set title to Occasionally(3,", {F('Honorific')}")}

    {set maxjump to status.fuel/ship.maxfuel}

    {if state.eddi_context_last_subject = "jump" && state.eddi_context_last_action = "complete" && maxjump < 1.25:
        Warning: {shipsfuel} almost depleted.
        {Pause(500)}

        {if scoop_present:
            {if system.scoopable:
                {set scoop_system to system.systemname}
            |else:
                {set detail to JumpDetails('total')}
                {set scoop_system to RouteDetails('scoop', detail.distance)}
                {set scoop_distance to Distance(scoop_system)}
            }

            {if scoop_system && scoop_system != "":
                Your nearest scoopable 
                {if scoop_system = system.name:
                    {set scoopable_bodies to []}
                    {for body in reportSystem.bodies:
                        {if body.scoopable:
                            {set scoopable_bodies to cat(scoopable_bodies, [P(body.shortname, "body")])}
                        }
                    }
                    {if len(scoopable_bodies) > 1:
                        stars are: 
                        {List(scoopable_bodies)}
                    |else:
                        star is:
                        {scoopable_bodies[0]}
                    }
                    {Pause(50)}
                    in the current system.
                |else:
                    star is in the {P(scoop_system, "starsystem")} system, {round(scoop_distance, 1)} lightyears away.
                }
                {Pause(500)}
                {if maxjump < 0.5:
                    {Occasionally(2, "Fuel")} scooping is extremely urgent {title}.
                |else:
                    {Occasionally(2, "Fuel")} scooping is strongly recommended {title}.
                }
            |else:
                Danger: There are no scoopable stars within your current jump range.
                {Pause(500)}
                Shutdown of non-essential systems is advised!
                {Pause(500)}
                Emergency transponder standing by {title}.
            }
        |else:
            {if maxjump < 0.5:
                {OneOf("Refuel now", "Refuelling is extremely urgent")} {title}.
            |else:
                {OneOf("Please refuel", "Refuelling is strongly recommended")} {title}.
            }
        }
    |elif currentfuel < 25:
        {OneOf("Warning:", "Caution:", "Attention:")}
        {shipsfuel} {OneOf("below 25%", "{currentpercent}" )} {title}.
    |elif currentfuel < 50:
        {shipsfuel} {OneOf("below 50%", "{currentpercent}" )} {title}.
    |elif currentfuel < 75:
        {Occasionally(2, "{shipsfuel} {currentpercent} {title}.")}
    |elif currentfuel < 100:
        {Occasionally(3, "{shipsfuel} {currentpercent} {title}.")}
    |else:
        {ShipName()} {shipsfuel} {OneOf(currentpercent, "at maximum")} {title}.
    }
}

