Galnet news published.script:
{_ set to 1 if you'd like interesting articles to be read to you _}
{set readArticles to 0}          

{_ define news categories that may be of interest to you _}
{_ Valid categories: Article, Powerplay, Community Goal, Starport Status Update, Week in Review _}
{set categories to [
    "Article",
    "Community Goal",
]}

{_ Process articles _}
{set interesting_news_title to []}
{set interesting_news_content to []}

{set item to 0}
{while item < len(event.items):
    {for category in categories:
        {if match(event.items[item].category, category):
            {set interesting_news_title to cat(interesting_news_title, [event.items[item].title])}
            {set interesting_news_content to cat(interesting_news_content, [event.items[item].content])}
        }
        {GalnetNewsMarkRead(event.items[item].content.id)}
        {set item to (item + 1)}
    }
}
{if len(event.items) = len(interesting_news_title): 
    {set allinteresting to 1} 
}

{_ Begin speaking _}

{if len(interesting_news_title) > 0:
    {if len(interesting_news_title) = 1:
        {set remarkable to OneOf('A noteworthy', 'A remarkable', 'An interesting', 'An intriguing', 'An unusual')}
        {OneOf('A', '{remarkable}' )}
        new Galnet article has been published, entitled {interesting_news_title[0]}.
    |elif len(event.items) > 1:
        {len(event.items)} 
        {if allinteresting: 
             {OneOf("noteworthy", "interesting", "intriguing", "remarkable", "unusual")}
        }
        new Galnet articles have been published
        {if !allinteresting:
             , including {len(interesting_news_title)} 
             {OneOf('of interest', 'of note', 'worthy of remark')}
        }.
    }
}

{if len(interesting_news_title) > 0:
    {if len(interesting_news_title) = 1 && readArticles:
        It reads: {interesting_news_content[0]}.
    |elif len(interesting_news_title) > 1:
        {set item to 0}
        {while item < len(interesting_news_title):
            {if item = 0:
                The first 
                is entitled {interesting_news_title[item]}
                {if readArticles:, and reads: {interesting_news_content[item]}}.
            |elif item = 1:
                The second
                is entitled {interesting_news_title[item]}
                {if readArticles:, and reads: {interesting_news_content[item]}}.
            |elif item = len(interesting_news_title) - 1:
                The last
                is entitled {interesting_news_title[item]}
                {if readArticles:, and reads: {interesting_news_content[item]}}.
            |else:
                The next
                is entitled {interesting_news_title[item]}
                {if readArticles:, and reads: {interesting_news_content[item]}}.
            }
            {set item to item + 1}
        }
    }
    {if !readArticles:
        {Pause(500)}
        The 
        {if len(interesting_news_title) > 1: 
            {OneOf("audio recordings", "news reports")} are 
        |else: 
            {OneOf("audio recording", "news report")} is
        } 
        ready {Occasionally(2, "for playback")}.
    }
}

