}.

Materials discard report.script:
{_ Find those that are above maximum, desired and minimum}
{set shoulddiscardmaterials to []}
{set coulddiscardmaterials to []}
{set lastdiscardmaterials to []}

{for material in materials:
    {if material.maximum && material.amount > material.maximum:
      {set shoulddiscardmaterials to cat(shoulddiscardmaterials, [material])}
    |elif material.desired && material.amount > material.desired:
      {set coulddiscardmaterials to cat(coulddiscardmaterials, [material])}
    |elif material.minimum && material.amount > material.minimum:
      {set lastdiscardmaterials to cat(lastdiscardmaterials, [material])}
    }
}

{if len(shoulddiscardmaterials) > 0:
    You are over your maximum level for {if len(shoulddiscardmaterials) = 1: 1 material |else: {len(shoulddiscardmaterials)} materials}.  You can discard
    {if len(shoulddiscardmaterials) = 1:
        {shoulddiscardmaterials[0].amount - shoulddiscardmaterials[0].maximum} {shoulddiscardmaterials[0].material}
    |elif len(shoulddiscardmaterials) = 2:
        {shoulddiscardmaterials[0].amount - shoulddiscardmaterials[0].maximum} {shoulddiscardmaterials[0].material} and {shoulddiscardmaterials[1].amount - shoulddiscardmaterials[1].maximum} {shoulddiscardmaterials[1].material}
    |else:
        {set cur to 0}
        {while cur < len(shoulddiscardmaterials):
            {if cur = 0:
                {shoulddiscardmaterials[cur].amount - shoulddiscardmaterials[cur].maximum} {shoulddiscardmaterials[cur].material}
            |elif cur < len(shoulddiscardmaterials) - 1:
                , {shoulddiscardmaterials[cur].amount - shoulddiscardmaterials[cur].maximum} {shoulddiscardmaterials[cur].material}
            |else:
                , and {shoulddiscardmaterials[cur].amount - shoulddiscardmaterials[cur].maximum} {shoulddiscardmaterials[cur].material}
            }
            {set cur to cur + 1}
        }
    }.
|elif len(coulddiscardmaterials) > 0:
    You are over your desired level for {if len(coulddiscardmaterials) = 1: 1 material |else: {len(coulddiscardmaterials)} materials}.  You can discard
    {if len(coulddiscardmaterials) = 1:
        {coulddiscardmaterials[0].amount - coulddiscardmaterials[0].desired} {coulddiscardmaterials[0].material}
    |elif len(coulddiscardmaterials) = 2:
        {coulddiscardmaterials[0].amount - coulddiscardmaterials[0].desired} {coulddiscardmaterials[0].material} and {coulddiscardmaterials[1].amount - coulddiscardmaterials[1].desired} {coulddiscardmaterials[1].material}
