        {set cur to 0}
        {while cur < len(coulddiscardmaterials):
            {if cur = 0:
                {coulddiscardmaterials[cur].amount - coulddiscardmaterials[cur].desired} {coulddiscardmaterials[cur].material}
            |elif cur < len(coulddiscardmaterials) - 1:
                , {coulddiscardmaterials[cur].amount - coulddiscardmaterials[cur].desired} {coulddiscardmaterials[cur].material}
            |else:
                , and {coulddiscardmaterials[cur].amount - coulddiscardmaterials[cur].desired} {coulddiscardmaterials[cur].material}
            }
            {set cur to cur + 1}
        }
    }.
|elif len(lastdiscardmaterials) > 0:
    You are over your minimum level for {if len(lastdiscardmaterials) = 1: 1 material |else: {len(lastdiscardmaterials)} materials}.  You can discard
    {if len(lastdiscardmaterials) = 1:
        {lastdiscardmaterials[0].amount - lastdiscardmaterials[0].minimum} {lastdiscardmaterials[0].material}
    |elif len(lastdiscardmaterials) = 2:
        {lastdiscardmaterials[0].amount - lastdiscardmaterials[0].minimum} {lastdiscardmaterials[0].material} and {lastdiscardmaterials[1].amount - lastdiscardmaterials[1].minimum} {lastdiscardmaterials[1].material}
    |else:
        {set cur to 0}
        {while cur < len(lastdiscardmaterials):
            {if cur = 0:
                {lastdiscardmaterials[cur].amount - lastdiscardmaterials[cur].minimum} {lastdiscardmaterials[cur].material}
            |elif cur < len(lastdiscardmaterials) - 1:
                , {lastdiscardmaterials[cur].amount - lastdiscardmaterials[cur].minimum} {lastdiscardmaterials[cur].material}
            |else:
                , and {lastdiscardmaterials[cur].amount - lastdiscardmaterials[cur].minimum} {lastdiscardmaterials[cur].material}
            }
            {set cur to cur + 1}
        }
    }.
|else:
    No materials can be discarded and remain within minimum levels.
}

