Mission accepted.script:
{SetState('eddi_context_last_subject', 'mission')}
{SetState('eddi_context_last_action', 'accepted')}

{set mission to MissionDetails(event.missionid)}

{if mission.type = "Donation":
    {OneOf("You have chosen","Mission accepted")} to {Occasionally(2,"generously")} donate
    {if mission.typeEDName = "Altruism": {mission.commodity} |else: credits} to
    {OneOf('improve','raise','increase')} your {OneOf('reputation','standing')} with {P(mission.faction, 'faction')}
|elif event.communal:
    You have accepted the community goal: {mission.localisedname}
|else:
    {if find(mission.typeEDName, "Passenger") > -1:
        {set type to "Transport "}
        {set type to cat(type, mission.passengertype)}
    |else:
        {set type to mission.type}
    }
    {set type to cat(type, " mission")}
    {OneOf("{type} {if mission.faction: for {P(mission.faction, 'faction')}} accepted",
        "{type} accepted {if mission.faction: for {P(mission.faction, 'faction')}}",
        "You have accepted a {type} {if mission.faction: for {P(mission.faction, 'faction')}}")
    }
}

{F("Mission faction state")}.

{if mission.passengerwanted:
    Warning! You are {OneOf("transporting","carrying")}
    {OneOf("{OneOf('wanted','illegal')} {OneOf('passengers','personnel')}","known felons")}!
    You may be attacked by {Occasionally(2,"system")} {OneOf("authority","police")}.
    Caution is advised.  Avoid being scanned.

|elif token(mission.name,"_",2) = "Skimmer":
    This is a surface {OneOf("mission","operation")}.

|elif token(mission.name,"_",2) = "Surface":
    This is a surface {OneOf("mission","operation")}, you will require an SRV.
}

{if !event.communal:
    {if missionsCount = 20:
        You have now reached the maximum number of missions you can take.
        Please complete some, in order to accept more.
    |else:
        {set reportMissionCount to:
            {OneOf(
                "You {Occasionally(2,'now')} have {missionsCount} 
                    {if missionsCount > 1: missions 
                    |else: mission
                    }",
                "{OneOf('That is','This is')} your
                    {if missionsCount = 1: 1st
                    |elif missionsCount = 2: 2nd
                    |elif missionsCount = 3: 3rd
                    |else: {missionsCount}th
                    }
                    mission"
            )}.
        }
        {Occasionally(max(cmdr.combatrating.rank - missionsCount, 1), reportMissionCount)}
    }
}

Mission check galaxy.script:
{_ Mission check galaxy }
{_ Check what systems you have missions in }


{_ Setup allmissions function _}
{set allmissions(system) to:
    {set here to find(systems, system)}
    {if here > -1:
        {if mission.statusEDName = "Active":       
            {set active to systems_active[here] + 1}
            {set systems_active to union(systems_active, [here:active])}
        |elif mission.statusEDName = "Claim":
            {set claim to systems_claim[here] + 1}
            {set systems_claim to union(systems_claim, [here:claim])}
            {set active to systems_active[here] + 1}
            {set systems_active to union(systems_active, [here:active])}
        }
    |else:
        {set systems to cat(systems, [system])}
        {if mission.statusEDName = "Active":
            {set systems_active to cat(systems_active, [1])}
            {set systems_claim to cat(systems_claim, [0])}
        |elif mission.statusEDName = "Claim":
            {set systems_active to cat(systems_active, [1])}
            {set systems_claim to cat(systems_claim, [1])}
         }
    }
}

{if missionsCount + goalsCount > 0:
    {set failed to 0}
    {set multi to 0}
    {set pending to 0}
    {set systems to []}
    {set systems_active to []}
    {set systems_claim to []}

    {for mission in missions:
        {if mission.statusEDName = "Failed":
            {set failed to failed + 1}
        |else:
            {set pending to pending + 1}
            {if mission.destinationsystems:
                {set multi to multi + 1}
                {for destination in mission.destinationsystems:
                    {allmissions(destination.name)}
                }
            |elif mission.destinationsystem && mission.destinationsystem != "":
                {allmissions(mission.destinationsystem)}
            }
        }
    }
    {set systemsCount to len(systems)}

    You have
    {if failed > 0:
        {failed} failed
        {if pending > 0:
            and
        |elif missionsCount + goalsCount = 1:
            mission.
        |else:
            missions.
        }
    }

    {if pending > 0:
        {if mission_report && systemsCount > 0:
            {set cur to 0}
            {while cur < systemsCount:
                {set active to systems_active[cur]}
                {set claim to systems_claim[cur]}

                {if active > 0 && active > claim:
                    {active} pending {if active = 1: mission |else: missions }
                    {if claim > 0:, including {claim} claimable {if claim = 1: mission |else: missions} }
                }
                {OneOf("at","in")} {P(systems[cur], "starsystem")}
                {set cur to cur + 1}
                {if cur = systemsCount - 1: and |elif cur < systemsCount: , }
            }.
        |else:
            {pending} pending {if missionsCount + goalsCount = 1: mission |else: missions} 
            {if systemsCount = 0:
                with no designated target {if missionsCount + goalsCount = 1: system |else: systems }
            |elif systemsCount = 1:
                in {if systems[0] = system.name: this |else: one } system
            |else:
                in {systemsCount} {Occasionally(2,"star")} systems
            }.
            {if multi > 0:
                {if multi = pending: All |else: {multi} } of these {if multi = 1: is a |else: are }
                multi-destination {if multi = 1: mission |else: missions }.
            }        
        }
    }
|else:
    {OneOf("You currently have no missions.", "You have no missions at the moment.",
        "You have no missions at this time.")}
}

Mission check station.script:
{_ Mission check station }
{_ Check what missions you have for the current station }
{if missionsCount + goalsCount > 0:
    {set current_station to state.eddi_context_body_name}
    {set active to 0}
    {set claim to 0}

    {if current_station && current_station != "":
        {for mission in missions:
            {if mission.destinationstation = current_station ||
                (mission.originstation = current_station && find(mission.type, "Delivery") > -1):
                {if mission.status = "Active":
                    {set active to active + 1}
                |elif mission.status = "Claim":
                    {set claim to claim + 1}
                }
            }
        }
    }

    {if active + claim > 0:
        {set description to []}
        {if active > 0:
            {set activeDescription to:
                 {active} {OneOf("pending", "active")}
            }
            {set description to cat(description, [activeDescription])}
        }
        {if claim > 0:
            {set claimDescription to:
                {claim} claimable
            }
            {set description to cat(description, [claimDescription])}
        }
    
        You have {List(description)} 
        {if active + claim = 1: mission |else: missions}
        {Occasionally(3,"located")} at this station
    }
}

Mission check system.script:
{_ Mission check system }
{_ Check what missions you have for the current system }

{_ Setup addstation function }
{set addstation(station) to:
    {set missions_here to missions_here + 1}
    {set station_based to station_based + 1}
    {set here to find(stations_here, station)}
    {if here > -1:
        {if mission.statusEDName = "Active":
            {set active to stations_active[here] + 1}
            {set stations_active to union(stations_active, [here:active])}
        |elif mission.statusEDName = "Claim":
            {set claim to stations_claim[here] + 1}
            {set stations_claim to union(stations_claim, [here:claim])}
        }
    |else:
        {set stations_here to cat(stations_here, [station])}
        {if mission.statusEDName = "Active":
            {set stations_active to cat(stations_active, [1])}
            {set stations_claim to cat(stations_claim , [0])}
        |elif mission.statusEDName = "Claim":
            {set stations_active to cat(stations_active, [0])}
            {set stations_claim to cat(stations_claim , [1])}
        }
    }
}

{if missionsCount + goalsCount > 0:
    {set missions_here to 0}
    {set station_based to 0}
    {set stations_active to []}
    {set stations_claim to []}
    {set stations_here to []}
    {set non_stations to []}

    {_ Find all missions in the current system and identify all the stations }
    {for mission in missions:
        {if mission.status != "Failed":
            {if mission.destinationsystems:
                {for destination in mission.destinationsystems:
                    {if destination.name = system.name && !destination.visited:
                        {set missions_here to missions_here + 1}
                        {if find(mission.typeEDName, "Passenger") > -1:
                            {set type to "Transport "}
                            {set type to cat(type, mission.passengertype)}
                        |else:
                            {set type to mission.type}
                        }

                        {if find(non_stations, type) = -1:
                            {set non_stations to cat(non_stations, [type])}
                        }
                    }
                }
            |elif mission.destinationsystem = system.name:
                {if mission.destinationstation && mission.destinationstation != "":
                    {addstation(mission.destinationstation)}
                |else:
                    {if mission.status = "Active":
                        {set missions_here to missions_here + 1}
                        {if find(mission.typeEDName, "Passenger") > -1:
                            {set type to "Transport "}
                            {set type to cat(type, mission.passengertype)}
                        |else:
                            {set type to mission.type}
                        }
                        {if find(non_stations, type) = -1:
                            {set non_stations to cat(non_stations, [type])}
                        }
                    }
                }
            |elif mission.originsystem = system.name && find(mission.type, "Delivery") > -1:
                {if mission.originstation && mission.originstation != "":
                    {addstation(mission.originstation)}
                }
            }
        }
    }

    {if missions_here > 0:
        {set missionSummaries to []}
        {set cur to 0}
        {for station in stations_here:
            {set station to StationDetails(station)}
            {set active to stations_active[cur]}
            {set claim to stations_claim[cur]}
            {set depot to stations_depot[cur]}

            {set summaries to []}
            {if active > 0:
                {set activeSummary to: 
                    {active} {OneOf("active", "pending")} {if active = 1: mission |else: missions}
                }
                {set summaries to cat(summaries, [activeSummary])}
            }
            {if claim > 0:
                {set claimSummary to: 
                    {claim} claimable {if claim = 1: mission |else: missions}
                }
                {set summaries to cat(summaries, [claimSummary])}
            }
            {if len(summaries) > 0:
                {set stationSummary to:
                    {List(summaries)} at 
                    {Occasionally(2, "the {P(station.model, 'station')}, ")}
                    {station.name}
                }
                {set missionSummaries to cat(missionSummaries, [stationSummary])}
            }
            {set cur to cur + 1}
        }
        
        {set remaining to missions_here - station_based}
        {if remaining > 0:
            {set otherMissions to:
                {remaining} {if len(missionSummaries) > 0: other}
                {if remaining > 1: missions |else: mission}
            }
            {set missionSummaries to cat(missionSummaries, [otherMissions])}
        }

        {if len(missionSummaries) > 0:
            {Pause(500)}
            You have
            {List(missionSummaries)}
            {OneOf("here", "in this system")}.
        }
    }
}

Mission completed.script:
{_ Mission completed }
{_ Triggered when you complete a mission }

{_ Context }
{SetState('eddi_context_last_subject', 'mission')}
{SetState('eddi_context_last_action', 'complete')}

{set mission to MissionDetails(event.missionid)}

{if find(mission.typeEDName, "Passenger") > -1:
    {set type to "Transport "}
    {set type to cat(type, mission.passengertype)}
|else:
    {set type to mission.type}
}
{set type to cat(type, " mission")}
{set ranktype to token(mission.name,"_",2)}
{set rewards to []}

{if mission.type = "Donation":
    {if mission.typeEDName = "Altruism":
        {set donated to cat(event.amount, " tonnes of ",event.commodity)}
    |else:
        {set donated to cat(Humanise(event.donation)," credits")}
    }

    {OneOf("Donated {Occasionally(2, '{donated}')} to {Occasionally(2, 'a grateful')} {P(mission.faction, 'faction')}",
        "{P(mission.faction, 'faction')} has received your donation {Occasionally(2, 'of {donated}')}",
        "{P(mission.faction, 'faction')} thanks you for your donation {Occasionally(2, 'of {donated}')}")}
|elif ranktype = "RANKEMP" || ranktype = "RankEmp":
    You have {OneOf("completed the Imperial {type} successfully","successfully completed the Imperial {type}")}
|elif ranktype = "RANKFED" || ranktype = "RankFed":
    You have {OneOf("completed the Federal {type} successfully","successfully completed the Federal {type}")}
|elif mission.communal:
    You have completed the community goal: {mission.localisedname}
|else:
    {type} {Occasionally(2, "successfully")} completed
}
{F("Mission faction state")}.

{if len(event.permitsawarded) > 0:
    {for permit in event.permitsawarded:
        {set permitReward to:
            {OneOf("a permit", "clearance")} {OneOf("for", "to visit")} the {P(permit, "starsystem")} system
        }
        {set rewards to cat(rewards, [permitReward])}
    }
}

{if len(event.commodityrewards) > 0:
    {for commodity in event.commodityrewards:
        {set commodityReward to:
            {commodity.amount}
            {if commodity.amount > 1: tons |else: ton} 
            of {commodity.commodity}
        }
        {set rewards to cat(rewards, [commodityReward])}
    }
}

{if len(event.materialsrewards) > 0:
    {for material in event.materialsrewards:
        {set materialReward to:
            {material.amount}
            {if material.amount > 1: units |else: unit} 
            of {material.material}
        }
        {set rewards to cat(rewards, [materialReward])}
    }
}

{if len(event.microresourcerewards) > 0:
    {for microresource in event.microresourcerewards:
        {set microresourceReward to:
            {microresource.amount}
            {if microresource.amount > 1: units |else: unit} 
            of {microresource.name}
        }
        {set rewards to cat(rewards, [microresourceReward])}
    }
}

{if event.reward > 0:
    {set creditReward to: {Humanise(event.reward)} credits }
    {set rewards to cat(rewards, [creditReward])}
}

{if len(rewards) > 0:
    You have {OneOf('received','been awarded')} {List(rewards)}
    {if event.faction:
        {Occasionally(3, 'from {P(event.faction, "faction")}')}
    }.
}

{_ Update to next system in route if conditions met _}
{Pause(1000)}
{set next_system to RouteDetails("update")}

