Route details.script:
{_ Route details _}
{_ Triggered when a route has been generated or updated _}


{_ Context _}
{SetState('eddi_context_search_system', event.system)}
{SetState('eddi_context_search_station', event.station)}
{SetState('eddi_context_search_distance', event.distance)}
{SetState('eddi_context_search_count', event.count)}

{set clip_notify to false}
{set type to event.routetype}
{if type = "encoded" || type = "facilitator" || type = "guardian"
    || type = "human" || type = "manufactured" || type = "raw":

    {if type = "facilitator":
        {set service to "interstellar factors contact"}
    |elif type = "encoded" || type = "manufactured" || type = "raw":
        {set service to cat(type, " materials trader")}
    |elif type = "guardian" || type = "human":
        {set service to cat(type, " technology broker")}
    }

    {if event.system && event.system != "":
        The nearest {service} is at {P(event.station, "station")}, in the
        {if event.system = system.name:
            current system
        |else:
            {set clip_notify to true}
            {P(event.system, "starsystem")} system, {round(event.distance, 1)} lightyears away
        }.
    |else:
        {service} not found within sensor range.
    }
|elif type = "cancel":
    {if !va_active:
        {if event.system && event.system != "":
            Cancelled destination setting to the {P(event.system, "starsystem")} system.
        }
    }
|elif type = "expiring":
    {if event.system && event.system != "":
        {set mission to MissionDetails(event.missionids[0])}

        {if find(mission.typeEDName, "Passenger") > -1:
            {set type to "Transport "}
            {set type to cat(type, mission.passengertype)}
        |else:
            {set type to mission.type}
        }
        {set type to cat(type, " mission")}

        {type} {if mission.faction: for {P(mission.faction, "faction")}}
        {if mission.destinationsystem: in the
            {if mission.destinationsystem = system.name:
                current
            |else:
                {set clip_notify to true}
                {P(mission.destinationsystem, "starsystem")}
            } system
        } is expiring next.
    |else:
        No active missions found.
    }
|elif type = "farthest":
    {if event.system && event.system != "":
        Your {if missionsCount = 1: only |else: farthest}
        mission target is in the
        {if event.system = system.name:
            current system
        |else:
            {set clip_notify to true}
            {P(event.system, "starsystem")} system, {round(event.distance, 1)} lightyears away
        }.
    |else:
        Mission not found with a designated target system.
    }
|elif type = "most":
    {if missionsCount = 1:
        Your only mission target is {Occasionally(2,"located")} in
        {if missions[0].destinationsystem = "":
            an unknown
        |elif missions[0].destinationsystem = system.name:
            the current
        |else:
            the {P(missions[0].destinationsystem, "starsystem")}
        }
        {Occasionally(2,"star")} system.
    |elif event.system && event.system != "":
        {set systems to split(event.route, "_")}
        {set systems_count to len(systems)}

        {if systems_count = 1:
            {if event.system = system.name:
                {set system_most to "The current system"}
            |else:
                {set clip_notify to true}
