System report.script:
{_ Fetch from Context, if a context is set _}
{if state.eddi_context_system_name:
    {set reportSystem to SystemDetails(state.eddi_context_system_name)}
|else:
    {set reportSystem to SystemDetails(system.systemname)}
}

{if !reportSystem.name || reportSystem.name = "":
    I'm not sure which system you are asking about.
|else:
    {P(reportSystem.name, "starsystem")} is
    {if reportSystem.population && reportSystem.population > 0:
        {if reportSystem.security = "High":
            {OneOf("a highly policed", "a well regulated")}
        |elif reportSystem.security = "Low":
            {OneOf("a poorly policed", "a poorly secured")}
        |elif reportSystem.security = "Anarchy":
            {OneOf("an unpoliced", "an unsecured")}
        |else:
            {if reportSystem.allegiance = "Federation": a |else: an }
        }
        {if reportSystem.allegiance = "Federation":
            Federal
        |elif reportSystem.allegiance = "Empire":
            Imperial
        |else:
            independent
        }
        {if reportSystem.government != "None":
            {reportSystem.government}
        |else:
            system
        }
        {OneOf("of", "with a population of", "home to", "supporting")} {Humanise(reportSystem.population)} {OneOf("people", "souls")}
        {if reportSystem.powerState.invariantName != "None":
            {if reportSystem.powerState.invariantName = "Home System":
                , and is home to {P(reportSystem.power, "power")}
            |elif reportSystem.powerState.invariantName = "Control":
                , and is {OneOf("allied with", "controlled by")} {P(reportSystem.power, "power")}
            |elif reportSystem.powerState.invariantName = "Exploited":
                , and is {OneOf("aligned to", "within the influence of")} {P(reportSystem.power, "power")}
            |elif reportSystem.powerState.invariantName = "Contested":
                , and is contested by multiple powers
            }
        }.

        {if reportSystem.faction:
            {P(reportSystem.faction, "faction")} is the {OneOf("controlling", "local", "immediate")} {OneOf("power", "faction")}.
        }

        {if len(reportSystem.stations) = 0:
            There are no stations present
        |else:
            {set systemStations to []}
            {if len(reportSystem.orbitalstations) = 1:
                {set orbitalStationDesc to:                
                    There is {OneOf("a single", "a lone", "just one", "one")} orbital station
                }
            |elif len(reportSystem.orbitalstations) > 1:
                {set orbitalStationDesc to:                
                    There are {len(reportSystem.orbitalstations)} orbital stations
                }
            }
            {if len(reportSystem.planetarystations) = 1:
                {set planetaryStationDesc to:            
                    {if len(reportSystem.orbitalstations) = 0: There is}
                    {OneOf("a single", "a lone", "just one", "one")} planetary station
                }
            |elif len(reportSystem.planetarystations) > 1:
                {set planetaryStationDesc to:            
                    {if len(reportSystem.orbitalstations) = 0: There are}
                    {len(reportSystem.planetarystations)} planetary stations
                }
            }
            {if len(reportSystem.carriersignalsources) = 1:
                {set fleetCarrierDesc to:
                    {if len(reportSystem.orbitalstations) = 0 && len(reportSystem.planetarystations) = 0: There is}
                    {OneOf("a single", "a lone", "just one", "one")} fleet carrier
                }
            |elif len(reportSystem.carriersignalsources) > 1:
                {set fleetCarrierDesc to:            
                    {if len(reportSystem.orbitalstations) = 0 && len(reportSystem.planetarystations) = 0: There are}
                    {len(reportSystem.carriersignalsources)} fleet carriers
                }
            }
            {if orbitalStationDesc:
                {set systemStations to cat(systemStations, [orbitalStationDesc])}
            }
            {if planetaryStationDesc:
                {set systemStations to cat(systemStations, [planetaryStationDesc])}
            }
            {if fleetCarrierDesc:
                {set systemStations to cat(systemStations, [fleetCarrierDesc])}
            }
            {List(systemStations)}
        }
        in this system
    |else:
       unpopulated
   }.
}

System scan complete.script:
{_ Preferences _}
{set reportGreenSystems to true}  {_ if true, reports systems containing all fsd synthesis elements. _}
{set reportGoldSystems to true}   {_ if true, reports systems containing all surface prospectable elements. _}
{set reportBodiesToMap to true}   {_ If true, recommends bodies to map using the `Bodies to map` script. _}

{if ship.Role.edname = "Exploration" || ship.Role.edname = "Multipurpose":
    {for compartment in ship.compartments:
        {if compartment.module.invariantName = "Planetary Vehicle Hangar":
            {set hasSRV to true}
        }
    }
    {if hasSRV:
        {if system.isgold:
            {if reportGoldSystems:
                This {Occasionally(2, "star")} system 
                {OneOf("contains", "holds", "has")}
                every element 
                {OneOf("that can be found", "available")}
                {OneOf("on planetary surfaces.", "from surface prospecting.")}
                Exploration class: Gold.
            }
        |elif system.isgreen:
            {if reportGreenSystems:
                This {Occasionally(2, "star")} system 
                {OneOf("contains", "holds", "has")}
                every element 
                {Occasionally(2, "that may be")} 
                required to 
                {OneOf("augment", "boost")} 
                {OneOf("the {Occasionally(2, \"jump\")} range of this ship.", 
                       "an explorer's {Occasionally(2, \"jump\")} range.", 
                       "{ShipName()}'s {Occasionally(2, \"jump\")} range."
                )}
                Exploration class: Green.
            }
        }
    }

    {if reportBodiesToMap:
        {set bodiesToMap to F("Bodies to map")}
        {if bodiesToMap:
            {Pause(2000)}
            {bodiesToMap}
        }
    }
}

System state changed.script:
{_ Context }
{SetState('eddi_context_last_subject', 'system')}
{SetState('eddi_context_last_action', 'change_state')}
{SetState('eddi_context_system', event.system)}
{SetState('eddi_context_system_system', event.system)}

{_ Set up our system name}
{if event.system = homesystem.name:
  {set system to OneOf('{P(event.system, "starsystem")}',
                       'your home system')}
|elif event.system = system.name:
  {set system to OneOf('{P(event.system, "starsystem")}',
                       'this system')}
|else:
  {set system to OneOf('system {P(event.system, "starsystem")}',
                       '{P(event.system, "starsystem")}')}
}

{if event.newstate != 'None':
  {_ System has entered a new state}
  {OneOf('Local transmissions report that', 'Information: ', 'Transmission intercepted.  Contents state that ')}
  {if event.newstate = 'Boom':
    {OneOf('{system} is experiencing a commercial boom',
           '{system} is entering a period of heightened commercial success')}
  |elif event.newstate = 'Bust':
    {system} is experiencing economic hardship
  |elif event.newstate = 'Civil Unrest':
    {OneOf('{system} is experiencing civil unrest',
           'there is unrest in the {system} system')}
  |elif event.newstate = 'Civil War':
    {OneOf('{system} is at war with another faction in the same system',
           'civil war has broken out in {system}',
           '{system} has fallen in to civil war',
           'hostilities have broken out in {system}')}
  |elif event.newstate = 'Election':
    {system} is undergoing elections
  |elif event.newstate = 'Expansion':
    {OneOf("a faction in {system}'s is attempting to expand in to a neighbouring system",
           "a faction in {system}'s is preparing to expand in to a neighbouring system")}
  |elif event.newstate = 'Famine':
    {system} is suffering from famine
  |elif event.newstate = 'Investment':
    {system}'s controlling faction are investing heavily in their local infrastructure
  |elif event.newstate = 'Lockdown':
    {system} has heightened security due to a lockdown by the controlling faction
  |elif event.newstate = 'Outbreak':
    {system} is suffering from an outbreak of contagious disease
  |elif event.newstate = 'Retreat':
    {system}'s controlling faction is retreating from a neighbouring system
  |elif event.newstate = 'War':
    {system} is now at war with a neighbouring system
  |else:
    {OneOf('{system} has entered {event.state}',
           '{system} is now in a state of {event.state}')}
  }.
}

System state report.script:
{_ Fetch from Context, if a context is set _}
{if state.eddi_context_system_name:
    {set reportSystem to SystemDetails(state.eddi_context_system_name)}
|else:
    {set reportSystem to SystemDetails(system.systemname)}
}

{if !reportSystem.name || reportSystem.name = "":

    I'm not sure which system you are asking about.

|else:

    {_ Report any ongoing conflicts _}
    {set conflictfactions to []}
    {if reportSystem.conflicts != null:
        {for conflict in reportSystem.conflicts:
            {P(conflict.faction1, "faction")}
            {if conflict.status = "active":
                are
            |elif conflict.status = "pending":
                will soon be
            |else:
                are recovering after
            }
            {if conflict.faction1dayswon > conflict.faction2dayswon:
               {if conflict.margin >= 3:
                   dominating
               |elif conflict.margin = 2:
                   winning
               |else:
                   ahead in
               }
            |elif conflict.faction2dayswon > conflict.faction1dayswon:
               {if conflict.margin >= 2:
                   losing
               |else:
                   trailing in
               }
            |else:
               tied in
            }
            {if StartsWithVowel(conflict.state): an |else: a }
            {conflict.state}
            with 
            {P(conflict.faction2, "faction")}
            {if conflict.stake:
               for control of {P(conflict.stake, "station")}
            }
            .
            {set conflictfactions to cat(conflictfactions, [conflict.faction1])}
            {set conflictfactions to cat(conflictfactions, [conflict.faction2])}
        }
    }

    {_ List our non-conflict faction states _}
    {set otherstates to []}
    {for faction in reportSystem.factions:
        {if faction.influence > 0 && find(conflictfactions, faction.name) = -1:
            {for presence in faction.presences:
                {if presence.systemName = reportSystem.name:
                    {if presence.FactionState.invariantName != "None":

                        {_ Note our controlling faction, if present in our `otherstates` list. _}
                        {if faction.name = reportSystem.faction:
                            {set factions to token(factions, "", index, "controlling ")}
                        }

                        {_ Add the faction to our list _}
                        {if has(otherstates, presence.state):
                            {set factions to otherstates[presence.state])}
                            {set factions to cat(factions, ",", P(faction.name, "faction"))}
                            {set otherstates to union(otherstates, [presence.state: factions])}
                        |else:
                            {set otherstates to union(otherstates, [presence.state: P(faction.name, "faction")])}
                        }
                    }
                }
            }
        }
    }

    {_ Sort from most factions with each state to least factions with each state. _}
    {declare by_items(a, b) as:
        {return cmp(len(split(b, ",")), len(split(a, ",")))}
    }
    {sort(otherstates, by_items)}

    {_ Prepare summaries for each state _}
    {set statesummaries to []}
    {for state, factions in otherstates:

        {set factions to split(factions, ",")}

        {set statesummary to:
            {if len(factions) > 1:
                {List(factions)} are
            |else:
                {factions[0]} are
            }
            {if state = "Retreat":
                in retreat
            |elif state = "War":
                at war
            |elif state = "Boom":
                undergoing a boom
            |elif state = "Civil Liberty":
                enjoying a period of heightened civil liberty
            |elif state = "Civil Unrest":
                experiencing civil unrest
            |elif state = "Civil War":
                in civil war
            |elif state = "Famine":
                in a state of famine
            |elif state = "Lockdown":
                in lockdown
            |elif state = "Outbreak":
                attempting to contain an outbreak of disease
            |elif state = "Expansion":
                gearing up to expand to another system
            |elif state = "Bust":
                financially bust
            |elif state = "Election":
                electing new leaders
            |elif state = "Investment":
                investing in its infrastructure
            |elif state = "Incursion":
                under assault by alien forces
            |elif state = "Pirate Attack":
                suffering from pirate attacks
            |elif state = "Blight":
                suffering from a crop blight
            |else: {_ catch-all, worded to avoid deciding between a/an}
                in a state of {state}
            }
        }
        {set statesummaries to cat(statesummaries, [statesummary])}
    }

    {_ Describe our non-conflict states _}
    {if len(statesummaries) > 0:
        {Pause(100)}
        {List(statesummaries)}
    }

}

Touchdown.script:
{_ Touchdown _}
{_ Triggered when your ship touches down on a planet's surface _}


{set body to BodyDetails(state.eddi_context_body_name, state.eddi_context_system_name)}

{if event.playercontrolled = true && state.eddi_context_last_subject != 'location':
  Touchdown 
  {if body.name: on {P(body.name, "body")} }.
|else:
  {ShipName()} has touched down
  {if body.name: on {P(body.name, "body")} }.
}

{Pause(2000)}

