FinesBounties_CheckShip.script:
{_ FinesBounties_CheckShip}
{_ Checks current ship for any crimes}

{if status.vehicle = "VRS" || status.vehicle = "Caça":
    {set v_type to "Sua nave atual"}
|else:
    {set v_type to cat(OneOf("Esta", "Sua"), " nave")}
}

{if len(state.eddi_context_bounties_type) > 0 && state.eddi_context_bounties_type != null && (find(state.eddi_context_bounties_type, "Fine") > -1 || find(state.eddi_context_bounties_type, "BountyInc") > -1):

    {set shipid to cast(state.eddi_context_currentshipid, "s")}
    {set shipname to ShipName(shipid)}

    {set bounties_shipid_array to split(state.eddi_context_bounties_shipid, "+=")}
    {set bounties_type_array to split(state.eddi_context_bounties_type, "+=")}
    {set bounties_amount_array to split(state.eddi_context_bounties_amount, "+=")}

    {_ Set up temp arrays of items that match "Fine" or "BountyInc"}

    {_ Set up variables}
    {set ship_fines_count to 0}
    {set ship_bounties_count to 0}
    {set ship_fines_total to 0}
    {set ship_bounties_total to 0}
    {set crime_array to []}

    {_ run loop from 0 to number of entries}
    {set cur to 0}
    {while cur < len(bounties_type_array):
        {if bounties_shipid_array[cur] = shipid:
            {if bounties_type_array[cur] = "Fine"
                {set ship_fines_count to ship_fines_count + 1}
                {set ship_fines_total to ship_fines_total + bounties_amount_array[cur]}
            |elif bounties_type_array[cur] = "BountyInc":
                {set ship_bounties_count to ship_bounties_count + 1}
                {set ship_bounties_total to ship_bounties_total + bounties_amount_array[cur]}
            }
        |else:
            {if bounties_type_array[cur] = "Fine" || bounties_type_array[cur] = "BountyInc":
                {set pos to find(crime_array, bounties_shipid_array[cur])}
                {if pos > -1:
                    {set pos to bounties_shipid_array[cur]}
                    {set amount to crime_array[pos] + bounties_amount_array[cur]}
                    {set crime_array to union(crime_array, [pos:amount])}
                |else:
                    {set pos to bounties_shipid_array[cur]}
                    {set amount to bounties_amount_array[cur]}
                    {set crime_array to union(crime_array, [pos:amount])}
                }
           }
       }
       {set cur to cur + 1}
    }

    {_ Report info}

    {if ship_bounties_count > 0: Cuidado: {vehicle} está procurado!}

    {if ship_fines_count = 1: {set N to:uma}
    |else: {set N to ship_fines_count}
    }

    {if ship_fines_count > 0:
        {if ship_bounties_count > 0: Ela |else: {v_type}}
        tem {N} multa{if ship_fines_count > 1:s} a pagar,
        num total de {ship_fines_total} créditos.
    }

    {if ship_bounties_count = 1: {set N to:uma}
    |else: {set N to ship_fines_count}
    }

    {if ship_bounties_count > 0:
        {if ship_fines_count > 0: Ela também |else: Ela}
        tem {N} recompensa{if ship_fines_count > 1:s |else:y} a pagar,
        num total de {ship_bounties_total} créditos.
    }

    {if len(crime_array) > 0:
        {if ship_fines_count = 0 && ship_bounties_count = 0:
            {v_type}
            {OneOf("está limpa. Entretanto, você tem mandados pendentes",
                   "não tem mandados pendentes. Entretanto, há alguns")}
            nas suas outras naves.
        |else:
            Você também tem mandados pendentes em
            {if len(crime_array) = 1: outra nave
            |else: suas outras naves
            }.
        }

        {set cur to 0}
        {set length to len(crime_array) - 1}
        {for id, amount in crime_array:
            {amount} credits on 
            {if ShipName(id) != "your ship": {ShipName(id)}
            |else: {ShipCallsign(id)}
            }
            {set cur to cur + 1}
            {if cur = length: and
            |elif cur < length:,
            }
        }
    }.

|else:
    {v_type} {OneOf("está limpa", "não tem mandados pendentes")}.
}

FinesBounties_ClearShip.script:
{_ FinesBounties_ClearShip _}
{_ Removes fines and bounties from ship, due to either death or ship sold _}

{if len(state.eddi_context_bounties_type) > 0 && state.eddi_context_bounties_type != null:

   {_ Use stored ident as this function can be called after a ship swap sale has changed it _}
   {set shipid to cast(state.eddi_context_currentshipid, "s")}

   {set bounties_shipid_array to split(state.eddi_context_bounties_shipid, "+=")}
   {set bounties_type_array to split(state.eddi_context_bounties_type, "+=")}

   {set position to 0}
   {for type in bounties_type_array:

      {if (type = "Fine" || type = "BountyInc")
          && bounties_shipid_array[position] = shipid
          && state.eddi_context_selfdestruct != "yes":

         {_ If Fine or Bounty Incurred, and not self destructed -> pay them all}

         {set delete to "yes"}
         {set temparray_type to token(state.eddi_context_bounties_type, "+=", position, "Delete")}
         {SetState('eddi_context_bounties_type', temparray_type)}

      |elif (type = "Bond" || type = "BountyAwd")
            && state.eddi_context_cmdrdied = "yes"):
         {_ If Bond or Bounty Awarded and player died -> lose them all}

         {set delete to "yes"}
         {set temparray_type to token(state.eddi_context_bounties_type, "+=", position, "Delete")}
         {SetState('eddi_context_bounties_type', temparray_type)}

      }
      {set position to position + 1}
   }
}

{SetState('eddi_context_cmdrdied', "no")}
{SetState('eddi_context_selfdestruct', "no")}

{if delete = "yes":

   {set bounties_shipid_array to split(state.eddi_context_bounties_shipid, "+=")}
   {set bounties_type_array to split(state.eddi_context_bounties_type, "+=")}
   {set bounties_faction_array to split(state.eddi_context_bounties_faction, "+=")}
   {set bounties_system_array to split(state.eddi_context_bounties_system, "+=")}
   {set bounties_amount_array to split(state.eddi_context_bounties_amount, "+=")}
   {set position to 0}

   {for type in bounties_type_array:
      {if type = "Delete":

         {_ Rebuild and modify text string from array}
         {set bounties_shipid_array to join(except(bounties_shipid_array, [position:""]), "+=")}
         {set bounties_type_array to join(except(bounties_type_array, [position:""]), "+=")}
         {set bounties_faction_array to join(except(bounties_faction_array, [position:""]), "+=")}
         {set bounties_system_array to join(except(bounties_system_array, [position:""]), "+=")}
         {set bounties_amount_array to join(except(bounties_amount_array, [position:""]), "+=")}

         {_ Rewrite State variable strings}
         {SetState('eddi_context_bounties_shipid', bounties_shipid_array)}
         {SetState('eddi_context_bounties_type', bounties_type_array)}
         {SetState('eddi_context_bounties_faction', bounties_faction_array)}
         {SetState('eddi_context_bounties_system', bounties_system_array)}
         {SetState('eddi_context_bounties_amount', bounties_amount_array)}

         {_ Re-split data}
         {set bounties_shipid_array to split(state.eddi_context_bounties_shipid, "+=")}
         {set bounties_type_array to split(state.eddi_context_bounties_type, "+=")}
         {set bounties_faction_array to split(state.eddi_context_bounties_faction, "+=")}
         {set bounties_system_array to split(state.eddi_context_bounties_system, "+=")}
         {set bounties_amount_array to split(state.eddi_context_bounties_amount, "+=")}
      |else:
         {set position to position + 1}
      }
   }
}

FinesBounties_Delete.script:
{_ FinesBounties_Delete}
{_ Remove paid/collected item from the memory store}

{set shipid to cast(state.eddi_context_currentshipid, "s")}

{if state.eddi_context_last_subject = "bond":
   {set type to "Bond"}
   {_ set amount to event.amount _}
|elif state.eddi_context_last_subject = "bounty":
   {set type to "BountyAwd"}
|elif state.eddi_context_last_subject = "bountypaid":
   {_ This is a placeholder until it's implemented in EDDI _}
   {set type to "BountyInc"}
|elif state.eddi_context_last_subject = "fine":
   {set type to "Fine"}
   {set amount to event.amount}
}

{set bounties_shipid_array to split(state.eddi_context_bounties_shipid, "+=")}
{set bounties_type_array to split(state.eddi_context_bounties_type, "+=")}
{set bounties_faction_array to split(state.eddi_context_bounties_faction, "+=")}
{set bounties_system_array to split(state.eddi_context_bounties_system, "+=")}
{set bounties_amount_array to split(state.eddi_context_bounties_amount, "+=")}

{if type = "BountyAwd" || type = "Bond":
   {for reward in event.rewards:

      {set position to 0}
      {for faction in bounties_faction_array:

         {_ If collecting at Interstellar Factors _}
         {set IF_amount to floor(bounties_amount_array[position] * 0.75)}

         {if (reward.faction = faction || reward.amount = IF_amount) && bounties_type_array[position] = type:

            {_ Rebuild data}
            {set bounties_shipid_array to join(except(bounties_shipid_array, [position:""]), "+=")}
            {set bounties_type_array to join(except(bounties_type_array, [position:""]), "+=")}
            {set bounties_faction_array to join(except(bounties_faction_array, [position:""]), "+=")}
            {set bounties_system_array to join(except(bounties_system_array, [position:""]), "+=")}
            {set bounties_amount_array to join(except(bounties_amount_array, [position:""]), "+=")}

            {_ Rewrite data}
            {SetState('eddi_context_bounties_shipid', bounties_shipid_array)}
            {SetState('eddi_context_bounties_type', bounties_type_array)}
            {SetState('eddi_context_bounties_faction', bounties_faction_array)}
            {SetState('eddi_context_bounties_system', bounties_system_array)}
            {SetState('eddi_context_bounties_amount', bounties_amount_array)}

            {_ Re-split data}
            {set bounties_shipid_array to split(state.eddi_context_bounties_shipid, "+=")}
            {set bounties_type_array to split(state.eddi_context_bounties_type, "+=")}
            {set bounties_faction_array to split(state.eddi_context_bounties_faction, "+=")}
            {set bounties_system_array to split(state.eddi_context_bounties_system, "+=")}
            {set bounties_amount_array to split(state.eddi_context_bounties_amount, "+=")}
         |else:
            {set position to position + 1}
         }
      }
   }

|elif type = "Fine" || type = "BountyInc":
   {_ If Fine and Amount match, delete it}

   {_ If paying at Interstellar Factors _}
   {set IF_amount to ceil(amount * 1.25)}

   {set position to 0}
   {for amounts in bounties_amount_array:

      {set amounts to cast(amounts, "n")}
      {set system to SystemDetails(system.name)}

      {if system.faction = bounties_faction_array[position]
          && bounties_type_array[position] = type
          && (amounts = amount || amounts = IF_amount):

         {_ Rebuild data}
         {set bounties_shipid_array to join(except(bounties_shipid_array, [position:""]), "+=")}
         {set bounties_type_array to join(except(bounties_type_array, [position:""]), "+=")}
         {set bounties_faction_array to join(except(bounties_faction_array, [position:""]), "+=")}
         {set bounties_system_array to join(except(bounties_system_array, [position:""]), "+=")}
         {set bounties_amount_array to join(except(bounties_amount_array, [position:""]), "+=")}

         {_ Rewrite data}
         {SetState('eddi_context_bounties_shipid', bounties_shipid_array)}
         {SetState('eddi_context_bounties_type', bounties_type_array)}
         {SetState('eddi_context_bounties_faction', bounties_faction_array)}
         {SetState('eddi_context_bounties_system', bounties_system_array)}
         {SetState('eddi_context_bounties_amount', bounties_amount_array)}

         {_ Re-split data}
         {set bounties_shipid_array to split(state.eddi_context_bounties_shipid, "+=")}
         {set bounties_type_array to split(state.eddi_context_bounties_type, "+=")}
         {set bounties_faction_array to split(state.eddi_context_bounties_faction, "+=")}
         {set bounties_system_array to split(state.eddi_context_bounties_system, "+=")}
         {set bounties_amount_array to split(state.eddi_context_bounties_amount, "+=")}
      |else:
         {set position to position + 1}
      }
   }
}

