GetDistance.script:
{_ GetDistance}
{_ Find distance to destination}

{set dist to -1}

{set fs to system.name}
{set ts to state.eddi_context_system_system}

{if fs && ts:
    {if ts = "":
        Origin system not designated.
    |elif fs = "":
        Destination system not designated.
    |else:
        {set fs to SystemDetails(fs)}
        {set ts to SystemDetails(ts)}

        {if !fs.name:
            Your origin is not in the Pilot's Federation star charts.
        |elif !ts.name:
            Your destination is not in the Pilot's Federation star charts.
        |else:
            {set dist to round(Distance(fs.x, fs.y, fs.z, ts.x, ts.y, ts.z), 2)}
            {if state.getdistance = 'true':
                Distance {Occasionally(2, "from {fs.name}")} to
                {Occasionally(2, "{OneOf('target','destination')} system")}
                {ts.name} is {dist} light years.
            }
        }
    }
}

{SetState('distance', dist)}

Glide.script:
{_ Context }
{SetState('eddi_context_last_subject', 'fsd')}
{SetState('eddi_context_last_action', 'glide')}
{SetState('eddi_context_system_name', event.system)}
{SetState('eddi_context_body_name', event.body)}
{SetState('eddi_context_body_system', event.system)}

{if event.gliding:
    {Pause(2000)}
    {Occasionally(2, "Modo de")} Planagem 
    {OneOf("ativado.", "iniciado.")}

    {set body to BodyDetails(event.body, event.system)}
    {if !body.name || body.name = "" || !body.gravity:
        Dados de aproximação planetária não estão disponíveis.
    |else:
        {if body.landable:
            {set gravity to round(body.gravity,2)}
            {if gravity > 4: Perigo! {set grav to "extremamente alta"}
            |elif gravity > 2: Cuidado! {set grav to "alta"}
            |elif gravity > 0.5: Aviso! {set grav to "média"}
            |else: {set grav to "baixa"}
            }
            {F("Body Name Tidy")}
            {body} é um mundo de gravidade {grav}, a {gravity} G.
        }
    }
|else:
    Planagem finalizada.
}

