Community goal.script:
{F('Honorific')}, 

{if len(event.name) > 1:
    {OneOf("you have expressed interest in", "I am tracking")}
    the following community goals.
}

{Pause(1000)}

{set cur to 0}
{while cur < len(event.name):

   {if cur = 0 && len(event.name) = 1:
      {_ nothing _}
   |elif cur = 0 && len(event.name) > 1:
      The first,
   |elif cur < (len(event.name) - 1):
      The next, 
   |else:
      The last,
   }

   {event.name[cur]},

   {if event.iscomplete[cur]:
      {OneOf("is complete", "has finished")} 
   |else: 
      {OneOf("is not yet complete", "is active")} 
   }
   {if event.tier[cur]:, and}

   {if event.tier[cur]:
       has reached {event.tier[cur]}.
       Your expected reward is {Occasionally(2, "calculated at")}
       {Humanise(event.tierreward[cur])} credits
       {Occasionally(2, "in the {event.percentileband[cur]}th percentile")}
   }.

    {if event.toprank[cur]: 
       You are one of {event.topranksize[cur]} top commanders in this event. 
    }

   {_ Create a comparison function _}
   {set compare(stat) to:     
       {if stat > 0.95 && stat < 1.05:
           {OneOf("approximately","roughly","nearly")} equal to 
       |elif stat > 2:
            {Humanise(stat)} times
       |elif stat < 1:
            {if stat > 0.89: {OneOf("fractionally","just")}}
            less than
       }
       {if stat < 0.01:
            just a {OneOf("fraction", "token")} of            
       |elif stat < 0.10:
            less than a tenth of
       |elif stat < 0.25:
            less than a quarter of
       |elif stat < 0.5:
            less than half of
       |elif stat < 0.75:
            less than
       |elif stat < 1:
            roughly equal to
       |elif stat < 1.25:
            a little more than
       |elif stat < 1.5:
            more than
       |elif stat < 2:
            significantly more than
       }
    }

    {set compare_contribution to (event.contribution[cur] / round(event.total[cur] / event.contributors[cur], 0))}
    {if compare_contribution != 0:
        {Occasionally(2, "You have contributed {compare(compare_contribution)} the average")}
        {OneOf(".", "and")}
    }

    {if !event.iscomplete[cur]:
       You may 
       {if event.contribution[cur] > 0: {OneOf("contribute more", "make additional contributions")} |else: contribute}
       at {P(event.station[cur])}, in
       {OneOf("{P(event.system[cur])}", "the {P(event.system[cur])} system" )}.
    }

    {set cur to cur + 1}
    {Pause(2000)}
}

